contract CureveDemo {

    address private amountIn = 0xdAC17F958D2ee523a2206206994597C13D831ec7;     
    address private amountOut = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
    address private curvePoolContract = 0xbEbc44782C7dB0a1A60Cb6fe97d0b483032FF1C7;
    address private owner;
    int128 private curveUsdtIndex = 2;
    int128 private curveDaiIndex = 0;
    
    constructor() public {
        owner = msg.sender;
    }
    
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }
    
    function get_dy(int128 amoutIndex,int128 amountOutIndex,uint256 amount,address _pool) external view returns(uint256){
           ICurve curve = ICurve(_pool);
           return curve.get_dy(amoutIndex,amountOutIndex,amount);
    }
    
    function _withdrawAllOut(address _address) public {
        uint amount = IERC20(amountOut).balanceOf(address(this));
        SafeERC20.safeTransfer(IERC20(amountOut),_address,amount);
    }
    
    function _withdrawAllIn(address _address)public {
        uint amount = IERC20(amountIn).balanceOf(address(this));
        SafeERC20.safeTransfer(IERC20(amountIn),_address,amount);
    }
    
     function exchange(int128 amoutIndex,int128 amountOutIndex,address _pool,uint _amount) external {
        SafeERC20.safeApprove(IERC20(amountIn),_pool,_amount);
        ICurve curve = ICurve(_pool);
        curve.exchange(amoutIndex, amountOutIndex,_amount,0);
    }
    
    function exchange_underlying(int128 amoutIndex,int128 amountOutIndex,address _pool,uint _amount) external {
        SafeERC20.safeApprove(IERC20(amountIn),_pool,_amount);
        ICurve curve = ICurve(_pool);
        curve.exchange_underlying(amoutIndex, amountOutIndex,_amount,0);
    }
    
     function setAmoutIn(address _address) public{
        amountIn = _address;
    }
    
     function setAmoutOut(address _address) public{
        amountOut = _address;
    }
    
}
