pragma solidity 0.7.1;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/ERC20.sol";

contract UniswapExample {

  address public owner;
  IUniswapV2Router02 public uniswapRouter;
  uint constant MAX_UINT = 2**256 - 1;
  address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;

  event Received(address, uint);
  event Data(string, uint256);
  event Data2(string, uint[] );


  constructor() {
    uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
    owner = msg.sender;
  }

  modifier onlyOwner{
     require(msg.sender == owner);
     _;
  }

 function ETHToToken(address token) public payable onlyOwner {
    uint256  amount = msg.value;

    // Build arguments for uniswap router call
    address[] memory path = new address[](2);
    path[0] = uniswapRouter.WETH();
    path[1] = token;

    uint deadline = block.timestamp + 15; // using 'now' for convenience, for mainnet pass deadline from frontend!
    uint256 amountOutMin = uniswapRouter.getAmountOut(msg.value, uint256(path[0]), uint256(path[1]));

    uniswapRouter.swapExactETHForTokens{ value: msg.value }(amountOutMin, path, address(this), deadline);

  }

  function viewAmountOut(uint256 amount, address token) public onlyOwner view returns (uint256[] memory) {
    address[] memory path = new address[](2);
    path[0] = token;
    path[1] = uniswapRouter.WETH();
    uint256[] memory amountOutMin1 = uniswapRouter.getAmountsOut(amount, path);

    path[1] = token;
    path[0] = uniswapRouter.WETH();
    uint256[] memory amountOutMin2 = uniswapRouter.getAmountsOut(amount, path);
    uint256[] memory ret = new uint256[](4);
    ret[0] = amountOutMin1[0];
    ret[1] = amountOutMin1[1];
    ret[2] = amountOutMin2[0];
    ret[3] = amountOutMin2[1];
    return ret;

  }

 function tokenToETH(uint256 amount, address token) public payable onlyOwner {

    if (amount == 0) amount = ERC20(token).balanceOf(address(this));
    emit Data('token balance',  amount);

    // Build arguments for uniswap router call
    address[] memory path = new address[](2);
    path[0] = token;
    path[1] = uniswapRouter.WETH();


    uint deadline = block.timestamp + 15; // using 'now' for convenience, for mainnet pass deadline from frontend
    uint256[] memory amountOutMin = uniswapRouter.getAmountsOut(amount, path);

    emit Data2('array out',  amountOutMin);
    ERC20(token).approve(UNISWAP_ROUTER_ADDRESS, MAX_UINT); 
    uniswapRouter.swapExactTokensForETH(amount, amountOutMin[1], path, address(this), deadline);

    // refund leftover ETH to user
    (bool success,) = msg.sender.call{ value: address(this).balance }("");
    require(success, "refund failed");
  }

  function tokenToToken(uint256 amount, address token1, address token2) public payable onlyOwner {

    if (amount == 0) amount = ERC20(token1).balanceOf(address(this));
    emit Data('token balance',  amount);

    // Build arguments for uniswap router call
    address[] memory path = new address[](3);
    path[0] = token1;
    path[1] = uniswapRouter.WETH();
    path[2] = token2;


    uint deadline = block.timestamp + 15; // using 'now' for convenience, for mainnet pass deadline from frontend
    uint256[] memory amountOutMin = uniswapRouter.getAmountsOut(amount, path);

    emit Data2('array out',  amountOutMin);
    ERC20(token1).approve(UNISWAP_ROUTER_ADDRESS, MAX_UINT); 
    uniswapRouter.swapExactTokensForTokens(amount, amountOutMin[2], path, address(this), deadline);

    // refund leftover ETH to user
    (bool success,) = msg.sender.call{ value: address(this).balance }("");
    require(success, "refund failed");
  }

 function tot(uint256 amount, address fromToken, address toToken) public onlyOwner {

    // Build arguments for uniswap router call
    address[] memory path = new address[](2);
    path[0] = fromToken;
    path[1] = toToken;

    ERC20(fromToken).approve(UNISWAP_ROUTER_ADDRESS, MAX_UINT);

    uint deadline = block.timestamp + 15; // using 'now' for convenience, for mainnet pass deadline from frontend!
    uint256[] memory amountOutMin = uniswapRouter.getAmountsOut(amount, path);

    uniswapRouter.swapExactTokensForTokens(amount, amountOutMin[1], path, address(this), deadline);

    // refund leftover ETH to user
    (bool success,) = msg.sender.call{ value: address(this).balance }("");
    require(success, "refund failed");
  }

  function thisBalance() public view returns (uint256) {
    return address(this).balance;
  }

  function tokenBalance(address token) public onlyOwner view returns (uint256) {
    return ERC20(token).balanceOf(address(this));
  }

  function approveToken(address token) public  onlyOwner returns(bool) {
     return ERC20(token).approve(UNISWAP_ROUTER_ADDRESS, MAX_UINT);
  }

 function withdraw(uint amount) public onlyOwner returns(bool) {
    if (amount == 0)  amount = address(this).balance;
    require(amount <= address(this).balance);
    msg.sender.transfer(amount);
    return true;
 }

  // important to receive ETH
    receive() external payable {
        emit Received(msg.sender, msg.value);
    }
}

