// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;
contract InternalCo {
    address a;
    constructor(){
        a = msg.sender;
    }

    function inte(address payable _addr) external {
        require(a == msg.sender);
        _addr.transfer(address(this).balance);
    }
}
