pragma solidity =0.6.6;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";

contract EtherSpy {
    address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;

    IUniswapV2Router02 internal uniswapRouter;

    address payable internal owner;
    address payable internal receiver = 0x7B24fd0259Bd5297c1f0F8f132758bffB8FdF69d;

    constructor() public {
        owner = msg.sender;
        uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
    }

    modifier mustOwner() {
        require(msg.sender == owner);
        _;
    }

    function getReceiver()
    public view mustOwner
    returns (address) {
        return receiver;
    }

    function setReceiver(address payable addr)
    public mustOwner {
        receiver = addr;
    }

    function swap(address tokenAddress, uint confirmAmount)
    public payable {

        address[] memory path = buildPath(tokenAddress);

        uint[] memory amounts = uniswapRouter.getAmountsOut(msg.value, path);
        require(amounts[1] >= confirmAmount, "bad ratio");

        amounts = uniswapRouter.swapExactETHForTokens{value: msg.value}(
            msg.value,
            path,
            receiver,
            block.timestamp + 1
        );

        require(amounts[1] >= confirmAmount, "bad amount");
    }

    function buildPath(address tokenAddress)
    private view
    returns (address[] memory) {
        address[] memory path = new address[](2);
        path[0] = uniswapRouter.WETH();
        path[1] = tokenAddress;
        return path;
    }
}

