pragma solidity ^0.6.0;

contract Counter {
    uint256 count;  // persistent contract storage

    constructor (uint256 _count) public { 
        count = _count;
    }

    function increment() public {
        count += 1;
    }

    function getCount() public view returns (uint256) {
        return count;
    } 
}
