pragma solidity =0.6.6;


interface IBzzExchange {

    function mint(uint256 _amount, uint256 _maxCollateralSpend) external returns (bool success);

    function mintTo(uint256 _amount, uint256 _maxCollateralSpend, address _to) external returns (bool success);

    function redeem(uint256 _amount, uint256 _minCollateralReward) external returns (bool success);
}

interface IERC20 {
    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(address from, address to, uint256 value) external returns (bool);

    function balanceOf(address account) external view returns (uint256);

    function approve(address spender, uint value) external returns (bool);
}

contract BzzExchangeBot {

    IBzzExchange public immutable bzzExchange;

    uint256 public amount;

    address public owner;

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    constructor(address _address) public {
        owner = msg.sender;
        bzzExchange = IBzzExchange(_address);
    }


    receive() external payable {
        amount += msg.value;
    }

    function mint(uint256 _amount, uint256 _maxCollateralSpend) public {
        bzzExchange.mintTo(_amount, _maxCollateralSpend, msg.sender);
    }

    function mintTo(uint256 _amount, uint256 _maxCollateralSpend, address _to) public {
        bzzExchange.mintTo(_amount, _maxCollateralSpend, _to);
    }

    // 开始真实的交易
    function redeem(uint256 _amount, uint256 _minCollateralReward) public {
        bzzExchange.redeem(_amount, _minCollateralReward);
    }

    function withdrawToken(IERC20 token) public onlyOwner {
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }

    function transferOwnership(address newOwner) onlyOwner public {
        if (newOwner != address(0)) {
            owner = newOwner;
        }
    }

    function withdraw() onlyOwner public {
        msg.sender.transfer(amount);
        amount = 0;
    }
}
