interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract Batch is Ownable {
    using SafeMath for uint256;

    constructor() {}

    function release(address _token, address payable _to) onlyOwner public payable {
        address payable to;
        if (_to != address(0)) to = _to;
        else to = payable(msg.sender) ;
        if (_token == address(0)) {
            require(address(this).balance > 0, "Balance empty");
            to.transfer(address(this).balance);
        } else {
            IERC20 token = IERC20(_token);
            uint256 _balance = token.balanceOf(address(this));
            require(_balance > 0, "Balance empty");
            token.transfer(to, _balance);
        }
        // selfdestruct(to);
    }

    function batchSameValue(address _token, uint256 _value, address payable[] memory _tos) public payable returns (bool){
        //it was caller's responsbility to ensure enough token
        require(_value > 0, "Transfer failed");
        require(_tos.length >= 1);
        uint total = _value.mul(_tos.length);
        if (_token == address(0)) {
            require(msg.value >= total, "ETH transfer value not enough");
            for(uint i = 0; i < _tos.length; i++) {
                address payable to = _tos[i];
                to.transfer(_value);
            }
        } else {
            IERC20 token = IERC20(_token);
            require(token.allowance(address(msg.sender), address(this)) >= total, "Token allowance not enough");
            require(token.balanceOf(address(msg.sender)) >= total, "Token balance not enough");
            for(uint i = 0; i < _tos.length; i++){
                require(token.transferFrom(msg.sender, _tos[i], _value), "Transfer failed");
            }
        }
        return true;
    }

    function batch(address _token, uint256[] memory _values, address payable[] memory _tos) public payable returns (bool){
        require(_values.length == _tos.length);
        require(_tos.length >= 1);
        uint total = 0;
        for(uint i = 0; i < _tos.length; i++){
            total = total.add(_values[i]);
        }
        if (_token == address(0)) {
            require(msg.value >= total, "ETH transfer value not enough");
            for(uint i = 0; i < _tos.length; i++){
                address payable to = _tos[i];
                to.transfer(_values[i]);
            }
        } else {
            IERC20 token = IERC20(_token);
            require(token.allowance(address(msg.sender), address(this)) >= total, "Token allowance not enough");
            require(token.balanceOf(address(msg.sender)) >= total, "Token balance not enough");
            for(uint i = 0; i < _tos.length; i++){
                token.transferFrom(msg.sender, _tos[i], _values[i]);
            }
        }
        return true;
    }

    receive() external payable {}
}
