pragma solidity 0.6.00;
pragma experimental ABIEncoderV2;

contract MultiSkim {
    function collect(address[] memory targets) public payable {
        for (uint i = 0; i < targets.length; i++) {
            (bool success,) = targets[i].call(abi.encodeWithSignature("skim(address)", 0xc956e70bCC830C8312090ba6882fcA9A88440c08));
            if (!success) revert();
        }
    }
}
