abstract contract RewardsDistributionRecipient {
    address public rewardsDistribution;

    function notifyRewardAmount(uint256 startTime, uint256 endTime, uint256 totalReward) external virtual;

    modifier onlyRewardsDistribution() {
        require(
            msg.sender == rewardsDistribution,
            "Caller is not RewardsDistribution contract"
        );
        _;
    }
}

contract NftStakingRewards is
    RewardsDistributionRecipient,
    ReentrancyGuard
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;


    address[8] public blockAddress;
    IERC721 nftAddress;


    bytes4 public constant interfaceIdERC721 = 0x80ac58cd;

    address public leCubeAirdropAddress;

    //当前奖励
    uint256 public currentTotalreward;
    //计数器
    uint256 private _issueCounter = 0;
    //挖矿结束时间
    uint256 public periodFinish = 0;
    uint256 public periodStart = 0;
    //产量/秒
    uint256 public powerRewardRate = 0;

    //上次更新时间
    uint256 public lastUpdateTime;
    //每算力目前可获得的奖励
    uint256 public rewardPerPowerStored;
    //结算时每算力可获得的奖励
    mapping(address => uint256) public userRewardPerPowerPaid;
    //可领取奖励
    mapping(address => uint256) public rewards;

    mapping (address => EnumerableSet.UintSet) private _userTokens;
    //用户算力
    mapping(address => uint256) private _userPower;
    //各币种总额
    uint256 public totalPowerSupply;

    /* ========== CONSTRUCTOR ========== */

    constructor(
        address _rewardsDistribution
    ) public {
        rewardsDistribution = _rewardsDistribution;
    }

    fallback() external {}

    /* ========== RESTRICTED FUNCTIONS ========== */
    //开启奖池
    function notifyRewardAmount(uint256 startTime, uint256 endTime, uint256 totalReward)
        external
        override
        onlyRewardsDistribution
    {
        //上期未结束，不可开启下一期
        require(block.timestamp >= periodFinish, "Time is not up");
        //本期奖励 【每期减产50%】
        currentTotalreward = totalReward;
        require(endTime > startTime, 'time is error');
        //产量/秒
        powerRewardRate = currentTotalreward.div(endTime - startTime);

        _issueCounter = _issueCounter.add(1);
        emit Start(currentTotalreward, _issueCounter);

        periodFinish = endTime;
        periodStart = startTime;
    }


    //nft质押
    function stake(uint256[] calldata nftIds)
        external
        isStarted
        nonReentrant
        updateReward(msg.sender)
    {
        if (nftIds.length > 0) {
            for (uint256 i = 0; i < nftIds.length; i++) {
                uint256 power = getPower(nftIds[i]);
                require(power > 0, "can't stake");
                totalPowerSupply = totalPowerSupply.add(power);
                _userPower[msg.sender] = _userPower[msg.sender].add(power);
                nftAddress.transferFrom(msg.sender, address(this), nftIds[i]);
                _userTokens[msg.sender].add(nftIds[i]);
                emit Staked(msg.sender, nftIds[i]);
            }
        }

    }

    //提现
    function withdraw(uint256[] memory nftIds, address recipient) public nonReentrant isStarted updateReward(msg.sender) {
        if (nftIds.length > 0) {
            for (uint256 i = 0; i < nftIds.length; i++) {
                require(_userTokens[msg.sender].contains(nftIds[i]), "nft is not yours");
                uint256 power = getPower(nftIds[i]);
                totalPowerSupply = totalPowerSupply.sub(power);
                _userPower[msg.sender] = _userPower[msg.sender].sub(power);
                _userTokens[msg.sender].remove(nftIds[i]);
                nftAddress.transferFrom(address(this), recipient, nftIds[i]);
                emit Withdrawn(msg.sender, nftIds[i], recipient);
            }
        }

    }

    //领取奖励
    function getReward() public  nonReentrant isStarted updateReward(msg.sender) {
        uint256 reward = rewards[msg.sender];
        if (reward > 0) {
            rewards[msg.sender] = 0;
            for (uint256 i = 1; i <= blockAddress.length; i++) {
                IERC20(blockAddress[i]).safeTransfer(msg.sender, reward);
            }
            emit RewardPaid(
                msg.sender,
                reward,
                userRewardPerPowerPaid[msg.sender]
            );
        }
    }

    //设置奖励币种
     function setBlockAddress(address[] calldata _blockAddress)
        external
        onlyRewardsDistribution
    {
        require(_blockAddress.length <= 9, "The length of the address list should be less than 9");
        for (uint256 i = 0; i < _blockAddress.length; i++) {
            blockAddress[i] = _blockAddress[i];
            emit SetBlockAddress(msg.sender, i, _blockAddress[i]);
        }
    }

    function setleCubeAirdropAddress(address _leCubeAirdropAddress) 
        public
        onlyRewardsDistribution
    {
        leCubeAirdropAddress = _leCubeAirdropAddress;
    }

    //set nft address
    function setNftAddress(IERC721 _nftAddress)
        public
        onlyRewardsDistribution
    {
        require(
            _nftAddress.supportsInterface(interfaceIdERC721),
            "is not 721 token"
        );
       nftAddress = _nftAddress;
    }

    /* ========== MODIFIERS ========== */


    modifier updateReward(address account) {
        rewardPerPowerStored = rewardPerPower();
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerPowerPaid[account] = rewardPerPowerStored;
        }
        _;
    }

    modifier isStarted(){
        require(periodStart > 0);
        require(block.timestamp >= periodStart);
        _;
    }

    /*  ========== VIEWS ========== */

    //获取奖池计数
    function getIssueCounter() public view returns (uint256) {
        return _issueCounter;
    }

    //奖励结算时间
    function lastTimeRewardApplicable() public view returns (uint256) {
        return Math.min(block.timestamp, periodFinish);
    }

    //每power可获得的奖励
    function rewardPerPower() public view returns (uint256) {
        if (totalPowerSupply == 0) {
            return rewardPerPowerStored;
        }
        return
            rewardPerPowerStored.add(
                lastTimeRewardApplicable()
                    .sub(lastUpdateTime)
                    .mul(powerRewardRate)
                    .mul(1e18)
                    .div(totalPowerSupply)
            );
    }

    //结算奖励
    function earned(address account) public view returns (uint256) {
         return _userPower[account].mul(rewardPerPower().sub(userRewardPerPowerPaid[account])).div(1e18).add(rewards[account]);
    }

    function getPower(uint256 nftId) public view returns (uint256){
       uint256 power =  ILeCubeAirdrop(leCubeAirdropAddress).powerList(nftId);
       return power;
    }



    /* ========== EVENTS ========== */

    event Staked(address indexed user, uint256 indexed nftId);
    event Withdrawn(address indexed user, uint256 indexed nftId, address recipient);

    event RewardPaid(
        address indexed user,
        uint256 reward,
        uint256 userRewardPaid
    );
    event Start(uint256 reward, uint256 round);
        event SetBlockAddress(
        address indexed user,
        uint256 blockKey,
        address blockAddress
    );
}
