pragma solidity =0.6.6;
contract Machine {

    constructor() public {
    }

    function myAdd(address router, uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external returns (uint[] memory amounts) {
        (bool success, bytes memory result) = router.delegatecall(abi.encodeWithSignature("swapExactTokensForTokens(uint256,uint256,address[],address,uint256)", amountIn, amountIn, path, to, deadline));
        return abi.decode(result, (uint[]));
    }
}
