// File: contracts/uniswapv2/interfaces/IUniswapV2Router01.sol

pragma solidity >=0.6.2;

interface IUniswapV2Router01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

// File: contracts/Treasury.sol

pragma solidity 0.6.12;




contract JuiceBarTreasury is Ownable {
    uint256 constant public MAX_UINT256 = 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF;

    IUniswapV2Router01 public router;
    address public fruit;
    address public juice;
    uint256 public nextConvertTime;
    uint256 public lastPrice;

    constructor(IUniswapV2Router01 _router, address _fruit, address _juice) public {
        router = _router;
        juice = _juice;
        fruit = _fruit;
        nextConvertTime = block.timestamp;
        lastPrice = getEstimatedETHforJuice();

        IERC20(fruit).approve(address(router), MAX_UINT256);
        IERC20(router.WETH()).approve(address(router), MAX_UINT256);
    }

    function convert() public  {
        uint256 currentPrice = getEstimatedETHforJuice();
        require(nextConvertTime <= block.timestamp);
        if (lastPrice < currentPrice) {
            sellFRUITForJUICE();
            burnJuice();
            nextConvertTime = block.timestamp + 23 hours;
        }
        lastPrice = currentPrice;
    }

    function sellFRUITForJUICE() private {
        uint256 amount = IERC20(fruit).balanceOf(address(this));
        address[] memory path = new address[](3);
        path[0] = fruit;
        path[1] = router.WETH();
        path[2] = juice;
        router.swapExactTokensForTokens(amount, uint(0), path, address(this), block.timestamp + 15);
    }

    function burnJuice() private {
        IERC20(juice).burn(IERC20(juice).balanceOf(address(this)));
    }

    function getEstimatedETHforJuice() public view returns (uint) {
        address[] memory path = new address[](2);
        path[0] = juice;
        path[1] = router.WETH();
        return router.getAmountsOut(1 ether, path)[0];
    }

    function setnextConvertTime(uint256 _nextConvertTime, bool withPrice) public onlyOwner {
        nextConvertTime = _nextConvertTime;
        if (withPrice) {
            lastPrice = getEstimatedETHforJuice();
        }
    }

    function close() public onlyOwner {
        address payable _addr = payable(address(owner()));
        IERC20(fruit).transfer(_addr, IERC20(fruit).balanceOf(address(this)));
        IERC20(juice).transfer(_addr, IERC20(juice).balanceOf(address(this)));
        selfdestruct(_addr); 
    }
}
