pragma solidity ^0.7.6;

abstract contract IERC20 {
    function totalSupply() virtual external view returns (uint256);
    function balanceOf(address account) virtual external view returns (uint256);
    function allowance(address owner, address spender)virtual external view returns (uint256);
    function transfer(address recipient, uint256 amount) virtual external returns (bool);
    function approve(address spender, uint256 amount) virtual external returns (bool);
    function decimals() virtual public view returns (uint8);
    function transferFrom(address sender, address recipient, uint256 amount ) virtual external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract Context {
    function _msgSender() internal view returns (address payable) {
        return msg.sender;
    }

    function _msgData() internal view returns (bytes memory) {
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
        return msg.data;
    }
}

contract Print is Context, IERC20 {
    using SafeMath for uint256;

    mapping (address => uint256) private _balances ;
    mapping (address => mapping (address => uint256)) private _allowances;
    string public constant name  = "Print";
    
    string public constant symbol = "PRINT";
    uint8 private constant _decimals = 18;
    uint256 _totalSupply = 1200000000000000000000;

    constructor() {  
	    _balances[msg.sender] = _totalSupply;
    }  
    
    function decimals() public override pure returns(uint8){return _decimals; }
    
    function totalSupply() override public view returns (uint256) {
	    return _totalSupply;
    }
    
    function balanceOf(address tokenOwner) override public view returns (uint) {
        return _balances[tokenOwner];
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function approve(address delegate, uint amount) override public returns (bool) {
        _allowances[msg.sender][delegate] = amount;
        emit Approval(msg.sender, delegate, amount);
        return true;
    }

    function allowance(address owner, address delegate) override public view returns (uint) {
        return _allowances[owner][delegate];
    }

    function transferFrom(address sender, address recipient, uint amount) override public returns (bool) {
        _transfer(sender, recipient, amount);
        require(amount <= _balances[sender]);    
        require(amount <= _allowances[sender][msg.sender]);
        return true;
    }
    
    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }
    
    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }
}

