pragma solidity ^0.8.0;


contract DropETH {
    function dropETH(address[] memory _addresses, uint _amount) public payable {
        require(msg.value >= _amount * _addresses.length, "not enough sent");
        for (uint i = 0; i < _addresses.length; i++) {
            require(payable(_addresses[i]).send(_amount));
        }
    }
}
