// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.7.6;

// iRUNE Interface
interface iRUNE {
    function transferTo(address, uint) external returns (bool);
}

// Sushiswap Interface
interface iPair {
    function sync() external;
}

contract Incentives {

    address public RUNE = 0x3155BA85D5F96b2d030a4966AF206230e46849cb;

    event Deposited(address indexed pair, uint value);

    constructor() {}

    // Deposit and sync
    function depositIncentives(address pair, uint value) public {
        iRUNE(RUNE).transferTo(pair, value);
        iPair(pair).sync();
        emit Deposited(pair, value);
    }
}
