pragma solidity ^0.6.6;

import './UniswapV2Library.sol';
import './interfaces/IUniswapV2Router02.sol';
import './interfaces/IUniswapV2Pair.sol';
import './interfaces/IUniswapV2Factory.sol';
import './interfaces/IERC20.sol';
import './interfaces/IERC20Extended.sol';

contract Migrator {
  function migrate(uint256 amount) external {}
  IERC20Extended public immutable fromToken = IERC20Extended(0x1F3f9D3068568F8040775be2e8C03C103C61f3aF);
  IERC20Extended public immutable toToken = IERC20Extended(0x1559FA1b8F28238FD5D76D9f434ad86FD20D1559);
}

contract Arbitrage {
  address public factory;
  uint constant deadline = 10 days;
  IUniswapV2Router02 public sushiRouter;

  Migrator archMigrator;

  constructor(address _factory, address _sushiRouter, address _migratorAddress) public {
    factory = _factory;  
    sushiRouter = IUniswapV2Router02(_sushiRouter);
    archMigrator = Migrator(_migratorAddress);
  }

  function startArbitrage(address token0, address token1, uint amount0, uint amount1) external {
    
    address pair1Address = IUniswapV2Factory(factory).getPair(token0, token1);
    require(pair1Address != address(0), 'This pool for pair1 does not exist');

    IUniswapV2Pair(pair1Address).swap(amount0, amount1, address(this), bytes('not empty'));
  }

  function uniswapV2Call(address _sender, uint _amount0, uint _amount1, bytes calldata _data) external {
    
    address[] memory path = new address[](2);
    uint amountToken = _amount0 == 0 ? _amount1 : _amount0; //amount borrowed
    
    address token0 = IUniswapV2Pair(msg.sender).token0();
    address token1 = IUniswapV2Pair(msg.sender).token1();

    require(msg.sender == UniswapV2Library.pairFor(factory, token0, token1), 'Unauthorized'); 
    require(_amount0 == 0 || _amount1 == 0);

    path[0] = _amount0 == 0 ? token1 : token0;
    path[1] = _amount0 == 0 ? token0 : token1;

    IERC20 token = IERC20(_amount0 == 0 ? token1 : token0); //Token borrowed
    token.approve(address(sushiRouter), amountToken); //Approve spend of borrowed token for borrowed amount

    //Make path from borrowed token to arch
    address[] memory ethArchPath = new address[](2);
    ethArchPath[0] = address(token);
    ethArchPath[1] = address(archMigrator.fromToken());

    //Swap token to ARCH
    uint amountRequired = UniswapV2Library.getAmountsIn(factory, amountToken, path)[0];
    uint amountReceived = sushiRouter.swapExactTokensForTokens(amountToken, amountRequired, path, msg.sender, deadline)[1];

    //Approve ARCH spend
    archMigrator.fromToken().approve(address(this), amountReceived); 

    //migrate arch
    uint fromBalance = archMigrator.fromToken().balanceOf(address(this));
    archMigrator.migrate(fromBalance);

    //Approve EDEN spend & get balance
    uint toBalance = archMigrator.toToken().balanceOf(address(this));
    archMigrator.toToken().approve(address(this), toBalance);

    //Make path from EDEN to borrowed token 
    address[] memory ethEdenPath = new address[](2);
    ethArchPath[1] = address(archMigrator.toToken());
    ethArchPath[0] = address(token);

    //Swap EDEN to borrowed token
    uint swapBackAmountRequired = UniswapV2Library.getAmountsIn(factory, toBalance, ethEdenPath)[0];
    sushiRouter.swapExactTokensForTokens(toBalance, swapBackAmountRequired, ethEdenPath, msg.sender, deadline)[1];
 
    token.transfer(msg.sender, amountRequired);

    uint change = token.balanceOf(address(this));
    if(change > 0)
    {
      token.transfer(tx.origin, change); 
    }
  }
}

