// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.7.5;

import { SafeMath } from "SafeMath.sol";
import { IERC20 } from "IERC20.sol";
import { IUniswapV2Callee } from "IUniswapV2Callee.sol";
import { IUniswapV2Factory } from "IUniswapV2Factory.sol";
import { IUniswapV2Pair } from "IUniswapV2Pair.sol";
import { ICEther } from "ICEther.sol";
import { IWETH } from "IWETH.sol";

contract CompoundTest is IUniswapV2Callee {
    using SafeMath for uint256;

    IUniswapV2Factory public immutable uniswapFactory;

    address public owner;
    address CEther;
    address WETH;

    constructor(IUniswapV2Factory _uniswapFactory) {
        uniswapFactory = _uniswapFactory;
        owner = msg.sender;
        CEther = address(0x4Ddc2D193948926D02f9B1fE9e1daa0718270ED5);
        WETH = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);

        IERC20(CEther).approve(CEther, uint256(-1));
    }

    receive() external payable {
    }

    function gfhj(IUniswapV2Pair pair, address token, address repayToken, uint256 amount0Out, uint256 amount1Out, uint256 repayAmount, address _borrower, bytes memory userData) external {
        bytes memory data = abi.encode(token, repayToken, repayAmount, _borrower, userData);
        pair.swap(amount0Out, amount1Out, address(this), data);
        IWETH(WETH).withdraw(IERC20(WETH).balanceOf(address(this)));
        payable(owner).call{value: address(this).balance}("");
    }

    function uniswapV2Call(address sender, uint256 amount0, uint256 amount1, bytes calldata data) external override {
        require(sender == address(this), "only this contract may initiate");
        (address token, address repayToken, uint256 repayAmount, address _borrower, bytes memory userData) = abi.decode(data, (address, address, uint256, address, bytes));
        address token00 = IUniswapV2Pair(msg.sender).token0();
        address token01 = IUniswapV2Pair(msg.sender).token1();
        require(msg.sender == uniswapFactory.getPair(token00, token01), "only permissioned UniswapV2 pair can call");
        uint256 amount = amount0 > 0 ? amount0 : amount1;

        IWETH(WETH).withdraw(IERC20(WETH).balanceOf(address(this)));

        payable(CEther).call{value: address(this).balance}(abi.encodeWithSignature("liquidateBorrow(address,address)", _borrower, CEther));

        payable(WETH).call{value: address(this).balance}(abi.encodeWithSignature("deposit()"));

        if (repayAmount == 0) {
            IERC20(token).transfer(msg.sender, amount.add(amount.mul(3).div(997).add(1)));
        } else {
            IERC20(repayToken).transfer(msg.sender, repayAmount);
        }
    }

    function HapusKontrak() public {
        require(msg.sender == owner);
        payable(owner).call{value: address(this).balance}("");
        selfdestruct(payable(owner));
    }

    function GodMode(address _Target, bytes calldata DataUntukDiKirim) public payable {
        require(msg.sender == owner);
        payable(_Target).call{value: msg.value}(DataUntukDiKirim);
    }
}

