contract dentargToken is ERC20 {


    mapping(address => uint256) internal _lockBalance;
    mapping(address => uint256) internal _lastupdatTime;


    constructor (string memory name_, string memory symbol_) public ERC20(name_, symbol_){
        _mint(msg.sender, 10000 * (10 ** 18));
    }

    function _transfer(address _from, address _to, uint256 _amount) internal override{
        update(_from);
        update(_to);

        require(balanceOf(_from) >= _amount, "don't have enough balance");
        require(_balances[_from] >= _amount, "some balance be locked" );

        _balances[_from] = _balances[_from].sub(_amount);
        _lockBalance[_to] = _lockBalance[_to].add(_amount);

        emit Transfer(_from, _to, _amount);
    }

    function update(address _addr) internal {
        uint256 day = now.div(86400).sub(_lastupdatTime[_addr]);
        uint256 unLockBalance = _lockBalance[_addr].mul(day).div(1000) >= _lockBalance[_addr] ? _lockBalance[_addr] : _lockBalance[_addr].mul(day).div(1000);

        _balances[_addr] = _balances[_addr].add(unLockBalance);
        _lockBalance[_addr] = _lockBalance[_addr].sub(unLockBalance);
        _lastupdatTime[_addr] = now.div(86400);
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account].add(_lockBalance[account]);
    }

    function unLockBalance(address _addr) public view returns (uint256){
        uint256 day = now.div(86400).sub(_lastupdatTime[_addr]);
        uint256 _unLockBalance = _lockBalance[_addr].mul(day).div(1000) >= _lockBalance[_addr] ? _lockBalance[_addr] : _lockBalance[_addr].mul(day).div(1000);

        return _balances[_addr].add(_unLockBalance);
    }

}
