pragma solidity >=0.7.0 <0.8.0;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IERC20.sol";

contract UniswapTrade {
  address internal constant UNISWAP_ROUTER_ADDRESS = 0xE592427A0AEce92De3Edee1F18E0157C05861564 ;
  IUniswapV2Router02 public uniswapRouter;


  constructor() {
    uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);

  }

  function BuyToken_zh(address[] memory _buypath,address _to)  public payable {
      uint deadline = block.timestamp + 25;
      address to=_to;
      address[] memory buypath=_buypath;
      uniswapRouter.swapExactETHForTokens{value:msg.value}(0, buypath, to, deadline);
  }
  receive() payable external {}

}
