contract ADeposit is ReentrancyGuard, Ownable{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address;

    address payable _collect;

    event InputAmount(address user, address token, uint256 amount); 
    event InputMainToken(address user, uint256 amount); 
    event OutputToken(address user, address token, uint256 amount);
    event OutputMainToken(address user, uint256 amount); 
    //0x67cd742Ec21323C7B6A51d0d0463C1a743aC3B57
    function updateCollect(address payable _address) public onlyOwner {
        _collect = _address;
    }
    function getCollect() public view returns(address) {
        return(_collect);
    }
    function inputToken(address token, uint256 amount) public nonReentrant {
        require(amount > 0, "amount is 0");
        IERC20(address(token)).safeTransferFrom(msg.sender, _collect, amount);
        emit InputAmount(msg.sender, token, amount);
    }

    function inputMainToken() public payable nonReentrant {
        require(msg.value > 0, "amount is 0");
        _collect.transfer(msg.value);
        emit InputMainToken(msg.sender, msg.value);
    }
}
