contract USDT is Ownable {
    //存款事件 传入(用户地址,存款数量,存款时间)
    event BANK_SAVINGS(address indexed userAddress, uint quantity, uint blockTime);
    //兑换事件 传入(用户地址,存款数量,兑换数量,兑换时间)
    event EXCHANGE(address indexed userAddress, uint quantity, uint exchangeCount, uint blockTime);


    //防溢出计算
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    //mfs地址(存入代币地址)
    IERC20 private mfsToken;
    //vcmfs地址(兑换出代币地址)
    IERC20 private usdtToken;
    //mfs总数
    uint256 private mfsCount;
    //存入上限
    uint256 private upper_Limit;
    //兑换率(3mfs:1vcmfs)
    uint256 private exchange;
    //精度
    uint256 private Precision;

    //是否超过上限
    modifier counter{
        require(mfsCount <= upper_Limit, 'MFS:Over limit');
        _;
    }

    constructor(IERC20 mfsAddress, IERC20 usdtAddress) public {
        //初始化mfs地址
        mfsToken = mfsAddress;
        //初始化vcmfs地址
        usdtToken = usdtAddress;
        //初始mfs总数
        mfsCount = 0;
        //初始可存入上限 40000*1e18
        upper_Limit = 40000 * (10 ** 18);
        //初始兑换率 2
        exchange = 2;
        uint jingdu = usdtToken.decimals();
        Precision = 10 ** jingdu;

    }

    /*
    存款
    传入 数量
    返回 是否成功
    */
    function deposit(uint mfsQuantity) external counter returns (bool){
        (uint _value, uint _usdt) = calculation(mfsQuantity);
        require(_value < _usdt, "USDT:Incoming MFS is greater than USDT balance");
        mfsToken.safeTransferFrom(msg.sender, address(this), mfsQuantity);
        (, mfsCount) = mfsCount.tryAdd(mfsQuantity);
        emit BANK_SAVINGS(msg.sender, mfsQuantity, block.timestamp);
        reward(mfsQuantity, _value);
        return true;
    }

    /*
    奖励给用户
    传入 奖励数量
    */
    function reward(uint mfsQuantity, uint usdtQuantity) internal returns (bool){
        usdtToken.safeTransfer(msg.sender, usdtQuantity);
        emit EXCHANGE(msg.sender, mfsQuantity, usdtQuantity, block.timestamp);
        return true;
    }

    /*
    计算
    传入 数量
    返回 可换出数量
    */
    function calculation(uint _quantity) internal view returns (uint value, uint _vcmfs){
        uint256 count = depositVcmfs();
        (, value) = ((_quantity.div(1e15)).tryDiv(exchange));
        return (value.mul(Precision).div(1e3), count);
    }


    /*------------------------- 设置类 -----------------------------*/
    /*
    转入vcmfs
    传入 数量
    返回 转入是否成功
    */
    function depositVcmfs() internal view returns (uint256){
        uint256 usdtCount = usdtToken.balanceOf(address(this));
        return usdtCount;
    }
    /*
      取出Token
      传入 数量
      返回 取出是否成功
      */
    function takeoutToken(address tokens, uint quantity) external onlyOwner returns (bool){
        IERC20(tokens).safeTransfer(msg.sender, quantity);
        return true;
    }

    /*
    销毁代币
    传入代币数量
    */
    function destroy(IERC20 tokenAddr) external onlyOwner returns (bool){
        if (tokenAddr == mfsToken) {
            tokenAddr = mfsToken;

        }
        tokenAddr.transfer(address(0), mfsCount);
        mfsCount = 0;
        return true;
    }

    /*
    修改存入代币地址
    传入 新代币地址
    */
    function setMfsTokenAddr(address mfsTokenAddr, address usdtTokenAddr) external onlyOwner {
        mfsToken = IERC20(mfsTokenAddr);
        usdtToken = IERC20(usdtTokenAddr);
        depositVcmfs();
    }
    /*
    修改上限
    传入 数量
    */
    function setUpperLimit(uint quantity) external onlyOwner {
        upper_Limit = quantity;
    }

    /*
    修改兑换率
    传入 十进制兑换率
    */
    function setExchange(uint _exchange) external onlyOwner {
        exchange = _exchange;
    }

    /*------------------------- 查看类 -----------------------------*/
    /*
    查看
    返回
    mfs地址(存入代币地址)
    vcmfs地址(兑换出代币地址)
    mfs总数
    vcmfs总数
    存入上限
    兑换率(3mfs:1vcmfs)
    */
    function getGameInfo() external view returns (IERC20, IERC20, uint, uint, uint, uint){
        uint256 count = depositVcmfs();
        return (
        mfsToken,
        usdtToken,
        mfsCount,
        count,
        upper_Limit,
        exchange
        );
    }

}
