        _;
    }

}

abstract contract Whitelist is Owned {

    event MemberAdded(address member);
    event MemberRemoved(address member);

    mapping (address => bool) public members;
    address[] whitelisted;

    modifier onlyWhitelisted() 
    {
        require(isMember(msg.sender));
        _;
    }

    function isMember(address _member)
        public
        view
        returns(bool)
    {
        return members[_member];
    }

    function getAllMember()
      external view 
      onlyOwner
    returns (address[] memory )
    {
         return whitelisted;

    }

    function addManyMembers(address[] calldata _members)
    public
    onlyOwner
    {
        for (uint i = 0; i < _members.length; i++) {

            require(!isMember(_members[i]), "Address is member already.");

            members[_members[i]] = true;
            whitelisted.push(_members[i]);
            emit MemberAdded(_members[i]);

        }
    }

    function addMember(address _member)
        public
        onlyOwner
    {
        require(
            !isMember(_member),
            "Address is member already."
        );

        members[_member] = true;
        whitelisted.push(_member);
        emit MemberAdded(_member);
    }

    function removeMember(address _member)
        public
        onlyOwner
    {
        require(
            isMember(_member),
            "Not member of whitelist."
        );

        for(uint i = 0; i<whitelisted.length; i++){
          if(_member == whitelisted[i]){
             delete whitelisted[i];
             break;
          } 
        }
        delete members[_member];
        emit MemberRemoved(_member);
    }

    function removeManyMembers(address[] calldata _members)
    public
    onlyOwner
    {
        for (uint i = 0; i < _members.length; i++) {

            require(
                isMember(_members[i]),
                "Not member of whitelist."
            );

            for(uint j = 0; j<whitelisted.length; j++){
              if(_members[i] == whitelisted[j]){
                 delete whitelisted[j];
                 break;
              } 
            }
            delete members[_members[i]];
            emit MemberRemoved(_members[i]);

        }
    }
}

contract Router is IRouter, Whitelist{

    address public immutable override WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    address factory = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;

    using SafeMath for uint;

    modifier ensure(uint deadline) {
        require(deadline >= block.timestamp, 'Router: EXPIRED');
        _;
    }

    constructor() Owned(msg.sender) {
    }

    function _swap(uint[] memory amounts, address[] memory path, address _to) internal virtual {
        for (uint i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = UniswapV2Library.sortTokens(input, output);
            uint amountOut = amounts[i + 1];
            (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOut) : (amountOut, uint(0));
            address to = i < path.length - 2 ? UniswapV2Library.pairFor(factory, output, path[i + 2]) : _to;
            IUniswapV2Pair(UniswapV2Library.pairFor(factory, input, output)).swap(
                amount0Out, amount1Out, to, new bytes(0)
            );
        }
    }

    // **** SWAP (supporting fee-on-transfer tokens) ****
    // requires the initial amount to have already been sent to the first pair
    function _swapSupportingFeeOnTransferTokens(address[] memory path, address _to) internal virtual {
        for (uint i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = UniswapV2Library.sortTokens(input, output);
            IUniswapV2Pair pair = IUniswapV2Pair(UniswapV2Library.pairFor(factory, input, output));
            uint amountInput;
            uint amountOutput;
            { // scope to avoid stack too deep errors
            (uint reserve0, uint reserve1,) = pair.getReserves();
            (uint reserveInput, uint reserveOutput) = input == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
            amountInput = IERC20(input).balanceOf(address(pair)).sub(reserveInput);
            amountOutput = UniswapV2Library.getAmountOut(amountInput, reserveInput, reserveOutput);
            }
            (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOutput) : (amountOutput, uint(0));
            address to = i < path.length - 2 ? UniswapV2Library.pairFor(factory, output, path[i + 2]) : _to;
            pair.swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }

    function swapExactETHForTokens(uint amountIn, uint amountOutMin, address[] calldata path, uint deadline)
    external 
    ensure(deadline)
    onlyWhitelisted returns (uint[] memory amounts){
        require(path[0] == WETH, 'Router: INVALID_PATH');
        amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path);
        require(amounts[amounts.length - 1] >= amountOutMin, 'Router: INSUFFICIENT_OUTPUT_AMOUNT');
        assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]));
        _swap(amounts, path, address(this));

    }

    function swapExactETHForTokensMutlipleTimes(uint amountIn, address[] calldata path, uint swapCount, uint deadline)
    external
    ensure(deadline)
    onlyWhitelisted returns (uint[] memory amounts)
    {
            require(path[0] == WETH, 'router: INVALID_PATH');
            for (uint i = 0; i < swapCount; i++) {

                amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path);
                assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]));
                _swap(amounts, path, address(this));

            }
    }


    function swapETHForExactTokensWithAmountInMax(uint amountOut, uint amountInMax, address[] calldata path, uint deadline)
    external
    ensure(deadline)
    onlyWhitelisted returns (uint[] memory amounts)
    {
        require(path[0] == WETH, 'router: INVALID_PATH');
        amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
        uint wethBalance = IWETH(WETH).balanceOf(address(this));
        require(amounts[0] <= wethBalance, 'router: EXCESSIVE_INPUT_AMOUNT 1');
        require(amounts[0] <= amountInMax, 'router: EXCESSIVE_INPUT_AMOUNT 2');
        assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]));
        _swap(amounts, path, address(this));
        // // refund dust eth, if any
        // if (wethBalance > amounts[0]) TransferHelper.safeTransferETH(address(this), wethBalance - amounts[0]);
    }

    function swapETHForExactTokens(uint amountOut, address[] calldata path, uint deadline)
    external
    ensure(deadline)
    onlyWhitelisted returns (uint[] memory amounts)
    {
        require(path[0] == WETH, 'router: INVALID_PATH');
        amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
        uint wethBalance = IWETH(WETH).balanceOf(address(this));
        require(amounts[0] <= wethBalance, 'router: EXCESSIVE_INPUT_AMOUNT');
        assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]));
        _swap(amounts, path, address(this));
        // // refund dust eth, if any
        // if (wethBalance > amounts[0]) TransferHelper.safeTransferETH(address(this), wethBalance - amounts[0]);
    }

    function swapETHForExactTokensMutlipleTimes(uint amountOut, address[] calldata path, uint swapCount, uint deadline)
    external
    ensure(deadline)
    onlyWhitelisted returns (uint[] memory amounts)
    {
            require(path[0] == WETH, 'router: INVALID_PATH');
            for (uint i = 0; i < swapCount; i++) {

                amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
                uint wethBalance = IWETH(WETH).balanceOf(address(this));
                require(amounts[0] <= wethBalance, 'router: EXCESSIVE_INPUT_AMOUNT');
                assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]));
                _swap(amounts, path, address(this));

           }
    }

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        uint deadline
    )
        external
        payable
        onlyWhitelisted 
        ensure(deadline)
    {
        require(path[0] == WETH, 'UniswapV2Router: INVALID_PATH');
        assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amountIn));
        uint balanceBefore = IERC20(path[path.length - 1]).balanceOf(address(this));
        _swapSupportingFeeOnTransferTokens(path, address(this));
        require(
            IERC20(path[path.length - 1]).balanceOf(address(this)).sub(balanceBefore) >= amountOutMin,
            'UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT'
        );
    }

    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, uint deadline)
    external 
    ensure(deadline)
    onlyWhitelisted returns (uint[] memory amounts){
        require(path[path.length - 1] == WETH, 'UniswapV2Router: INVALID_PATH');
        amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path);
        require(amounts[amounts.length - 1] >= amountOutMin, 'UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT');
        TransferHelper.safeTransfer(
            path[0], UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, address(this));
    }

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
    uint amountIn,
    uint amountOutMin,
    address[] calldata path,
    uint deadline
    )
        external
        ensure(deadline)
        onlyWhitelisted
    {
        require(path[path.length - 1] == WETH, 'UniswapV2Router: INVALID_PATH');
        TransferHelper.safeTransfer(
            path[0], UniswapV2Library.pairFor(factory, path[0], path[1]), amountIn
        );
        _swapSupportingFeeOnTransferTokens(path, address(this));
        uint amountOut = IERC20(WETH).balanceOf(address(this));
        require(amountOut >= amountOutMin, 'UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT');
    }


    function swapETsFETH(address[] calldata path, uint deadline)
    external 
    ensure(deadline) 
    onlyWhitelisted returns (uint[] memory amounts){
        require(path[path.length - 1] == WETH, 'UniswapV2Router: INVALID_PATH');
        uint amountIn = IERC20(path[0]).balanceOf(address(this));
        amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path);
        require(amounts[amounts.length - 1] > 0, 'UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT');
        TransferHelper.safeTransfer(
            path[0], UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, address(this));
    }

    function calculatePercentage( uint theNumber, uint bp) public pure returns  (uint) {
        return theNumber * bp / 10000;
    }


    function swapETsFETHConfigurableAmount(address[] calldata path, uint bp, uint deadline)
    external 
    ensure(deadline) 
    onlyWhitelisted returns (uint[] memory amounts){
        require(path[path.length - 1] == WETH, 'Router: INVALID_PATH');
        uint amountIn = IERC20(path[0]).balanceOf(address(this));
        uint inputAmount = calculatePercentage(amountIn, bp);
        amounts = UniswapV2Library.getAmountsOut(factory, inputAmount, path);
        require(amounts[amounts.length - 1] > 0, 'Router: INSUFFICIENT_OUTPUT_AMOUNT');
        TransferHelper.safeTransfer(
            path[0], UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, address(this));
    }

    // Deposit WETH to contract
    function depositWETH() external payable{
        IWETH(WETH).deposit{value: msg.value}();
    }

    // receive() payable external { require(msg.sender == WETH, 'WETH_ONLY'); }
    receive() payable external {}

     // Withdraw WETH to address.
    function transferWETH(address payable to, uint amountIn) external onlyOwner returns (bool){
        uint wethBalance = IWETH(WETH).balanceOf(address(this));
        require(wethBalance > 0 , 'Router: INSUFFICIENT_BALANCE');
        require(wethBalance >= amountIn, 'Router: INSUFFICIENT_BALANCE');
        if (wethBalance > 0 && wethBalance >= amountIn){
            IWETH(WETH).withdraw(amountIn);
            (bool success,) = to.call{value: amountIn}('');
            require(success, 'WITHDRAW_TO_CALL_FAILED');
        }
        return true;
    }

    // Show the contract WETH balance
    function balanceOfWETH() external view returns (uint){
        uint wethBalance = IWETH(WETH).balanceOf(address(this));
        return wethBalance;
    }

    function checkErc20TokenBalance(address token) external view onlyWhitelisted returns(uint) {
        IERC20 erc20Token = IERC20(token);
        return erc20Token.balanceOf(address(this));
    }

    function transferErc20Token(address token, address to) external onlyOwner returns (bool){
        IERC20 erc20Token = IERC20(token);
        uint tokenBalance = erc20Token.balanceOf(address(this));
        require(tokenBalance > 0 , 'Router: INSUFFICIENT_BALANCE');
        if (tokenBalance > 0){
            bool success= erc20Token.transfer(to, tokenBalance);
            require(success, 'WITHDRAW_TO_CALL_FAILED');
        }
        return true;
    }

    function contractAddress()
    external view
    returns (address)
    {
      return address(this);
    }
}
