/**
 * @title ERC1155Tradable
 * ERC1155Tradable - ERC1155 contract that whitelists an operator address, 
 * has create and mint functionality, and supports useful standards from OpenZeppelin,
  like _exists(), name(), symbol(), and totalSupply()
 */
contract ERC1155Tradable is ERC1155 {
    using Strings for string;

    uint256 private _currentTokenID = 0;
    mapping(uint256 => address) public creators;
    mapping(uint256 => uint256) public tokenSupply;
    mapping(uint256 => uint256) public tokenMaxSupply;
    // Contract name
    string public name = "Rekt Anniversary Tickets";
    // Contract symbol
    string public symbol = "RAT01";

    uint256[] tokensId = [0, 1, 2];
    uint256[] amounts = [500, 250, 250];

    constructor() ERC1155(tokensId, amounts) {

        for(uint i = 0; i < tokensId.length; i++) {
            creators[tokensId[i]] = msg.sender;
            tokenSupply[tokensId[i]] = amounts[i];
            tokenMaxSupply[tokensId[i]] = amounts[i];
        }
        _currentTokenID = 2;
    }

    function uri(uint256 _id) public view override returns (string memory) {
        require(_exists(_id), "ERC721Tradable#uri: NONEXISTENT_TOKEN");
        return Strings.strConcat(super.uri(_id), Strings.uint2str(_id));
    }

    /**
     * @dev Returns the total quantity for a token ID
     * @param _id uint256 ID of the token to query
     * @return amount of token in existence
     */
    function totalSupply(uint256 _id) public view returns (uint256) {
        return tokenSupply[_id];
    }

    /**
     * @dev Returns the max quantity for a token ID
     * @param _id uint256 ID of the token to query
     * @return amount of token in existence
     */
    function maxSupply(uint256 _id) public view returns (uint256) {
        return tokenMaxSupply[_id];
    }

    /**
     * Override isApprovedForAll to whitelist user's OpenSea proxy accounts to enable gas-free listings.
     */
    function isApprovedForAll(address _owner, address _operator)
        public
        view
        override
        returns (bool isOperator)
    {
        return ERC1155.isApprovedForAll(_owner, _operator);
    }

    /**
     * @dev Returns whether the specified token exists by checking to see if it has a creator
     * @param _id uint256 ID of the token to query the existence of
     * @return bool whether the token exists
     */
    function _exists(uint256 _id) internal view returns (bool) {
        return creators[_id] != address(0);
    }

    /**
     * @dev calculates the next token ID based on value of _currentTokenID
     * @return uint256 for the next token ID
     */
    function _getNextTokenID() private view returns (uint256) {
        return _currentTokenID + 1;
    }

    /**
     * @dev increments the value of _currentTokenID
     */
    function _incrementTokenTypeId() private {
        _currentTokenID++;
    }
}

/**
 * @title RektMerchNFT Contract
 */
contract RektMerchNFT is ERC1155Tradable, ReentrancyGuard {

    // 0 hat + np
    // 1 hat + np + medium tshirt
    // 2 hat + np + large tshirt
    uint[3] prices = [0.05 ether, 0.1 ether, 0.1 ether];
    address rektOffice = 0x3C5c2F4bCeC51a36494682f91Dbc6cA7c63B514C;
    uint deployTime;

    mapping(address => mapping(uint => bool)) buyers;

    event BoughtOne(address indexed buyer, uint merchId);
    event BurnedOne(address indexed buyer, uint merchId);

    constructor() {
        deployTime = block.timestamp;
    }

    function buyMerch(uint _merchId) external payable nonReentrant {
        require(!buyers[msg.sender][_merchId], 'One pack buy per address');
        require(balanceOf(address(this), _merchId) > 0, 'Sold out or not exist');
        require(msg.value == prices[_merchId],'Not try to rekt us');
        buyers[msg.sender][_merchId] = true;

        // Send eth to the rekt office
        (bool success, ) = rektOffice.call{value: address(this).balance}('');
        require(success, 'Something wrong');

        // Transfer NFT to the buyer
        _safeTransferFrom(address(this), msg.sender, _merchId, 1, '');
        emit BoughtOne(msg.sender, _merchId);
    }

    function redeemMerch(uint _merchId) external {
        require(block.timestamp > deployTime + 7 days,'Not allowed yet');
        require(balanceOf(msg.sender, _merchId) > 0, 'Not an holder');

        // Burn the nft
        _burn(msg.sender, _merchId, 1);
        emit BurnedOne(msg.sender, _merchId);
    }
}
