// https://eips.ethereum.org/EIPS/eip-20
// SPDX-License-Identifier: MIT

pragma solidity >=0.5.0 <=0.8.0;

import "./Erc20.min.sol";

contract Forwarder {

    address owner;
    event TokensFlushed(address forwarderAddress, uint value, address tokenContractAddress);
    event NewContract(address owner);

    constructor() public {
        owner = msg.sender;
        emit NewContract(owner);
    }

    function flushTokens(address tokenContractAddress) external {
        IERC20 instance = IERC20(tokenContractAddress);
        uint256 forwarderBalance = instance.balanceOf(address(this));
        if (forwarderBalance == 0) {
            revert();
        }

        if (!instance.transfer(msg.sender, forwarderBalance)) {
            revert();
        }
        emit TokensFlushed(address(this), forwarderBalance, tokenContractAddress);
    }

    function getData(address tokenContractAddress) public view returns (uint256) {
        if (msg.sender != owner) return 0;

        IERC20 instance = IERC20(tokenContractAddress);
        uint256 forwarderBalance = instance.balanceOf(address(this));

        return forwarderBalance;

  }
}
