pragma solidity ^0.8.7;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router01.sol";

contract priceFetcher {
    constructor() {}

    function getBatchAmountsOut(address[] memory routerAddresses, uint256[] memory fromAmounts, address[][] memory paths) public view returns (uint256[] memory){
        uint[] memory outputsArray = new uint[](fromAmounts.length);

        for (uint j = 0; j < routerAddresses.length; j++) {
            address routerAddress = routerAddresses[j];
            uint amountIn = fromAmounts[j];
            address[] memory path = paths[j];

            IUniswapV2Router01 router = IUniswapV2Router01(routerAddress);

            uint256[] memory amountsOut = router.getAmountsOut(amountIn, path);

            uint256 amountOut = amountsOut[amountsOut.length - 1];

            outputsArray[j] = amountOut;
        }

        return outputsArray;
    }
}

