// SPDX-License-Identifier: RANDOM_TEXT

pragma solidity ^0.6.6;
 
contract LongRecord{
    mapping (uint => string) pic;
    uint x = 0;

    address public _owner;

      constructor(address owner) public {
        _owner = owner;
      }
  

    function record(string memory s) public{
        require(msg.sender == _owner);
        pic[x]=s;
        x++;
    }
    
    function getRecord(uint i) view public returns (string memory){
        return pic[i];
    }
    
}
