// Root file: contracts/Mint/RefReward.sol

pragma solidity ^0.6;

// import "@openzeppelin/contracts/math/SafeMath.sol";

interface IRef {
    function scoreOf(address a) external view returns (uint256);
}

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);

    function transfer(address to, uint256 value) external returns (bool);
}

contract RefReward {
    using SafeMath for uint256;

    address public developer;
    uint8 public first_ratio = 10;

    mapping(address => uint256) public claimed;
    IRef public constant ref = IRef(0x08b8C77A770BbE8E19A8b2E7d90516F0ED7a6D57);
    IERC20 public constant token = IERC20(
        0x2c4Cd2DeCa2397B12f0982eee383C56895dfD31b
    );

    IERC20 public sgToken = IERC20(0x523EEAfdaC23FA2CB0C3873CdA4818cdc80e28E8);

    uint256 public sgTokenLimit = 10 * 1e18;

    modifier onlyDeveloper() {
        require(msg.sender == developer, "You're not developer");
        _;
    }

    constructor() public {
        developer = msg.sender;
    }

    function claim() external {
        if (sgToken.balanceOf(msg.sender) >= sgTokenLimit) {
            uint256 score = ref.scoreOf(msg.sender);
            uint256 delta = score - claimed[msg.sender];
            token.transfer(msg.sender, delta.mul(first_ratio).div(100));
            claimed[msg.sender] = score;
        }
    }

    function setSgTokenLimit(uint256 _limit) external onlyDeveloper {
        sgTokenLimit = _limit;
    }
}
