pragma solidity 0.6.12;

import { FlashLoanReceiverBase } from "./FlashLoanReceiverBase.sol";
import { ILendingPool, ILendingPoolAddressesProvider, IERC20 } from "./Interfaces.sol";
import { SafeMath } from "./Libraries.sol";
import { IWETHGateway } from "./aave-protocol-v2/contracts/misc/interfaces/IWETHGateway.sol";
import { IUniswapV2Router02 } from "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
/** 
    !!!
    Never keep funds permanently on your FlashLoanReceiverBase contract as they could be 
    exposed to a 'griefing' attack, where the stored funds are used by an attacker.
    !!!
 */
contract AaveWatch { //is FlashLoanReceiverBase {
    using SafeMath for uint256;

    address internal constant owner = 0x32eC8541BB748d7E23d3ec2f24CD06054C13a4ec; // kaikas address
    address internal constant IWETHGateway_ADDR = 0xcc9a0B7c43DC2a5F023Bb9b738E45B0Ef6B06E04;
    address internal constant ILendingPoolAddressesProvider_ADDR = 0xB53C1a33016B2DC2fF3653530bfF1848a515c8c5;
    address internal constant IUniswapV2Router02_ADDR = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address internal constant USDT_ADDR = 0xdAC17F958D2ee523a2206206994597C13D831ec7;
    
    IWETHGateway public weth_gateway;
    IUniswapV2Router02 public uniswap_router_v2;
    ILendingPoolAddressesProvider public lending_pool_address_privider;
    ILendingPool public LENDING_POOL;
    // constructor(ILendingPoolAddressesProvider _addressProvider) FlashLoanReceiverBase(_addressProvider) public {
    // constructor(ILendingPoolAddressesProvider _addressProvider) FlashLoanReceiverBase(_addressProvider) public {
    //     weth_gateway = IWETHGateway(IWETHGateway_ADDR);
    //     // lending_pool_address_privider = ILendingPoolAddressesProvider(ILendingPoolAddressesProvider_ADDR);
    //     uniswap_router_v2 = IUniswapV2Router02(IUniswapV2Router02_ADDR);
    // }
    constructor() public {
        weth_gateway = IWETHGateway(IWETHGateway_ADDR);
        lending_pool_address_privider = ILendingPoolAddressesProvider(ILendingPoolAddressesProvider_ADDR);
        LENDING_POOL = ILendingPool(lending_pool_address_privider.getLendingPool());
        uniswap_router_v2 = IUniswapV2Router02(IUniswapV2Router02_ADDR);
    }

    function approveTo(address tokenA, address exchange) internal {
        uint256 allowed = IERC20(tokenA).allowance(address(this), exchange);
        if (allowed < 2**256 - 1) {
            IERC20(tokenA).approve(exchange, 2**256 - 1);
        }
    }


    function rebalance(uint256 health_target) public {
        
        (uint256 total_collateral_eth, uint256 total_debt_eth, uint256 available_borrow_eth, uint256 current_liquidation_threshold, uint256 tlv, uint256 health_factor) = LENDING_POOL.getUserAccountData(msg.sender);

        // calc withdraw ETH_amount
        // w = (debt * health_target - 0.825 * collateral) / (health_target - 0.825)
        //   = (1000 * debt * health_target - 825 * collateral) / (1000 * health_target - 825)
        //uint256 withdraw_eth_amount = (total_debt_eth * health_target * 1000 - total_collateral_eth * 825) / (health_target * 1000  - 825);
        uint256 withdraw_eth_amount = total_debt_eth.mul(health_target).mul(1000).sub(total_collateral_eth.mul(825)).div(health_target.mul(1000).sub(825));
        
        //uint256 health_tmp = (total_collateral_eth - withdraw_eth_amount) / total_debt_eth * (825 / 1000);
        uint256 health_tmp = total_collateral_eth.sub(withdraw_eth_amount).div(total_debt_eth).mul(825).div(1000);
        if (health_tmp > 10**18) {
            // function withdrawETH(address lendingPool, uint256 amount, address to)        
            LENDING_POOL.withdraw(uniswap_router_v2.WETH(), withdraw_eth_amount, msg.sender);
    
            // move to this contract
            IERC20(uniswap_router_v2.WETH()).transferFrom(msg.sender, address(this), withdraw_eth_amount);
            
            // ETH -> USDT exchange
            address[] memory path = new address[](2);
            path[0] = uniswap_router_v2.WETH();        
            path[1] = USDT_ADDR;
    
            uint256[] memory amounts_repay = new uint256[](2);
            uint256 amountOut = 1;
            approveTo(uniswap_router_v2.WETH(), address(uniswap_router_v2));
            amounts_repay = uniswap_router_v2.swapExactTokensForTokens(withdraw_eth_amount, amountOut, path, address(this), block.timestamp);
            
            // uint256 amountPay = IERC20(USDT_ADDR).balanceOf(address(this));
            
            // function repay(address asset, uint256 amount, uint256 rateMode, address onBehalfOf) external returns (uint256);
            // @param rateMode The interest rate mode at of the debt the user wants to repay: 1 for Stable, 2 for Variable
            approveTo(USDT_ADDR, address(LENDING_POOL));
            LENDING_POOL.repay(USDT_ADDR, amounts_repay[1], 2, msg.sender);
        }
        else {
            
        }

    }

}

