pragma solidity ^0.8.0;


contract MiddleWare{ 

    address payable private admin;
    address private owner;

    constructor(address payable _admin){
        owner = msg.sender;
        admin = _admin;
    }

    event Deposit(address indexed from, uint indexed id, uint indexed _value);

    fallback() external{  }

    function updateAdmin(address payable _newAdmin) external returns(bool){
        require(msg.sender == owner,"Sender not Owner.");
        admin = _newAdmin;
        return true;
    }

    function deposit(uint _id) payable external returns(bool){
        require(msg.value>0,"Value needs to be not 0");

        if(!admin.send(msg.value)) revert();

        emit Deposit(msg.sender, _id, msg.value);
        return true;
    }
}
