pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "DydxFlashloanBase.sol";
import "ICallee.sol";
import "Ownable.sol";

import "IERC20.sol";

interface Unitroller {
    function enterMarkets(address[] calldata cTokens) external returns (uint[] memory);
}

interface Erc20 {
    function approve(address, uint256) external returns (bool);

    function transfer(address, uint256) external returns (bool);
}

interface CErc20Delegator {
    function mint(uint256) external returns (uint256);

    function borrow(uint borrowAmount) external returns (uint);
    
    function borrowBalanceCurrent(address) external returns (uint256);

    function exchangeRateCurrent() external returns (uint256);

    function supplyRatePerBlock() external returns (uint256);

    function redeem(uint) external returns (uint);

    function redeemUnderlying(uint) external returns (uint);
    
    function repayBorrow(uint256) external returns (uint256);
}

contract DydxFlashloaner is ICallee, DydxFlashloanBase, Ownable {

   constructor() public Ownable() {
   }
   
    struct MyCustomData {
        address token;
        address unitroller;
        address supplyDelegator;
        address borrowDelegator;
        uint256 ltv;
        uint256 leverageAmount;
        uint256 repayAmount;
        bool close;
    }

    function callFunction(
        address sender,
        Account.Info memory account,
        bytes memory data
    ) public override {
        MyCustomData memory mcd = abi.decode(data, (MyCustomData));
        uint256 balOfLoanedToken = IERC20(mcd.token).balanceOf(address(this));
        
        //By default there will be 16 wei locked up in the contract
        require(
            balOfLoanedToken > 16,
            "There are no funds in the contract"
        );

        if (!mcd.close) {
            address[] memory markets =  new address[](1);
            markets[0] = mcd.supplyDelegator;
            Unitroller(mcd.unitroller).enterMarkets(markets);
    
            Erc20(mcd.token).approve(mcd.supplyDelegator, mcd.leverageAmount);
            CErc20Delegator(mcd.supplyDelegator).mint(mcd.leverageAmount);
    
            require(
                mcd.leverageAmount * mcd.ltv / 10000 >= mcd.repayAmount - balOfLoanedToken,
                "Aborting the loan due to incorrect collateralization factor"
            );
    
            CErc20Delegator(mcd.borrowDelegator).borrow(mcd.repayAmount - balOfLoanedToken);
        } else {
            CErc20Delegator borrowDelegator = CErc20Delegator(mcd.borrowDelegator);
            borrowDelegator.repayBorrow(mcd.leverageAmount);
        }

        require(
            balOfLoanedToken >= mcd.repayAmount,
            "Not enough funds to repay dydx loan!"
        );
    }


