    //ltv % (e.g. 7500 for 75%)
    function openPosition(address _solo, address _token, address _unitroller, address _supplyDelegator, address _borrowDelegator, uint256 _amount, uint _ltv)
        external onlyOwner
        returns (bool)
    {
        uint256 allowance = IERC20(_token).allowance(msg.sender, address(this));
        require(
            allowance >= _amount,
            "Contract not authorised for the required amount"
        );
        
        // Put all funds into Smart Contract
        IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        
        //Calculate leverage amount which is the amount to borrow from DxDy
        //This assumes that we need to return +2 wei;
        //Since we want to have extra funds for the closing position we will aim to reserve 16 wei (_amount -10) in the contract at the end of the openPosition call
        uint256 leverageAmount = (_amount - 10) * (10000/(10000 - _ltv));
        
        // Get marketId from token address
        ISoloMargin solo = ISoloMargin(_solo);
        uint256 marketId = _getMarketIdFromTokenAddress(_solo, _token);

        // Calculate repay amount (_amount + (2 wei))
        // Approve transfer from
        uint256 repayAmount = _getRepaymentAmountInternal(leverageAmount);
        IERC20(_token).approve(_solo, repayAmount);

        Actions.ActionArgs[] memory operations = new Actions.ActionArgs[](3);

        operations[0] = _getWithdrawAction(marketId, leverageAmount);
        operations[1] = _getCallAction(abi.encode(MyCustomData(
            {
                token: _token, 
                unitroller: _unitroller,
                supplyDelegator: _supplyDelegator,
                borrowDelegator: _borrowDelegator,
                ltv: _ltv,
                leverageAmount: leverageAmount,
                repayAmount: repayAmount,
                close: false
            }
        )));
        operations[2] = _getDepositAction(marketId, repayAmount);

        Account.Info[] memory accountInfos = new Account.Info[](1);
        accountInfos[0] = _getAccountInfo();

        solo.operate(accountInfos, operations);
        
        return true;
    }
    
    
    function closePosition(address _solo, address _token, address _unitroller, address _supplyDelegator, address _borrowDelegator)
        external onlyOwner
        returns (bool)
    {
        CErc20Delegator borrowDelegator = CErc20Delegator(_borrowDelegator);
        uint outstandingBalance = borrowDelegator.borrowBalanceCurrent(address(this));

        // Get marketId from token address
        ISoloMargin solo = ISoloMargin(_solo);
        uint256 marketId = _getMarketIdFromTokenAddress(_solo, _token);

        // Calculate repay amount (_amount + (2 wei))
        // Approve transfer from
        uint256 repayAmount = _getRepaymentAmountInternal(outstandingBalance);
        IERC20(_token).approve(_solo, repayAmount);

        Actions.ActionArgs[] memory operations = new Actions.ActionArgs[](3);

        operations[0] = _getWithdrawAction(marketId, outstandingBalance);
        operations[1] = _getCallAction(abi.encode(MyCustomData(
            {
                token: _token, 
                unitroller: _unitroller,
                supplyDelegator: _supplyDelegator,
                borrowDelegator: _borrowDelegator,
                ltv: 0,
                leverageAmount: outstandingBalance,
                repayAmount: repayAmount,
                close: true
            }
        )));
        operations[2] = _getDepositAction(marketId, repayAmount);

        Account.Info[] memory accountInfos = new Account.Info[](1);
        accountInfos[0] = _getAccountInfo();

        solo.operate(accountInfos, operations);
        
        return true;
    }
    
    
    function redeem(uint256 _amount, bool _redeemType, address _delegator) 
        public onlyOwner 
        returns (bool)
    {
        // Create a reference to the corresponding cToken contract
        CErc20Delegator cToken = CErc20Delegator(_delegator);

        // `amount` is scaled up by 1e18 to avoid decimals

        uint256 redeemResult;

        if (_redeemType == true) {
            // Retrieve your asset based on a cToken amount
            redeemResult = cToken.redeem(_amount);
        } else {
            // Retrieve your asset based on an amount of the asset
            redeemResult = cToken.redeemUnderlying(_amount);
        }

        return true;
    }

    function repayBorrow(address _delegator, uint256 _amount)
        public onlyOwner
        returns (bool)
    {
        CErc20Delegator token = CErc20Delegator(_delegator);
        
        uint outstandingBalance;
        if (_amount == 0) {
            outstandingBalance = token.borrowBalanceCurrent(address(this));
        } else {
            outstandingBalance = _amount;
        }
        
        token.repayBorrow(outstandingBalance);
        return true;
    }
    
    function supply(address _delegator, address _token, uint256 _amount)
        public
        returns (bool)
    {
        IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        Erc20(_token).approve(_delegator, _amount);
        CErc20Delegator(_delegator).mint(_amount);
        
        return true;
    }
    
    function approve(address _token, address _spender, uint256 _amount)
        public onlyOwner
        returns (bool)
    {
        Erc20(_token).approve(_spender, _amount);
        
        return true;
    }
