pragma solidity ^0.8.0;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/IERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/ERC20.sol";

contract Blockrunner {
    address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
   
    mapping (address => bool) internal members;
    address private _owner;

    /**
     * Necessary interfaces.
     */
    IUniswapV2Router02 public uniswapRouter;
    IERC20 public token;

    /**
     * Creates an instance of the UniswapV2 Router02 contract.
     * Also adds the contract creator to the list of members.
     */
    constructor() {
        _owner = msg.sender;
        uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
        // Add the owner to the members mapping
        members[msg.sender] = true;
    }

    /**
     * Access modifier to restrict access to certain methods to only the owner.
     */
    modifier restricted(address _account) {
        require(msg.sender == _account, "Sender not authorized");
        _;
    }
    
    /**
     * _isMember is a private function that returns whether an address
     * is already a member or not.
     */
    function _isMember(address _member) private view returns(bool) {
        return members[_member];
    }
    
    /**
     * addMember adds an address to the members mapping.
     * It is restricted to the owner and the new address can't be a member already.
     */
    function addMember(address _member) public restricted(_owner) {
        require(!_isMember(_member), "Already a member");
        members[_member] = true;
    }

    /**
     * getBalance returns the contract's ETH balance.
     */
    function getBalance() public view returns(uint) {
        return address(this).balance;
    }

    /**
     * withdraw transfers an amount back to the sender. It is restricted
     * to the owner.
     */
    function withdraw(uint amount) public restricted(_owner) {
        payable(msg.sender).transfer(amount);
    }

    /**
     * swap uses the Router02 contract to swap ETH for tokens using swapExactETHForTokens.
     * _targetToken is the target token address, amountOutMin is the minimum number of tokens
     * wanted (required by the swapExactETHForTokens method), and deadline is the
     * Unix timestamp that marks the end of this swap request.
     * 
     * It required the caller to be a member.
     */
    function swap(address _targetToken, uint _amountOutMin, uint _deadline) public {
        require(_isMember(msg.sender), "Not authorized");
        // Path for the swap
        address[] memory path = new address[](2);
        path[0] = uniswapRouter.WETH();
        path[1] = _targetToken;
        // Buy all the tokens the contract can afford
        uint[] memory amounts = uniswapRouter.swapExactETHForTokens{value: address(this).balance}(_amountOutMin, path, address(this), _deadline);
        _transfer(_targetToken, amounts[1]);
    }

    /**
     * _transfer implements the ERC20 interface for the target token
     * and transfers it back to the original owner.
     */ 
    function _transfer(address _tokenAddress, uint _tokenAmount) private {
        token = ERC20(_tokenAddress);
        // Always transfer the tokens back to the original owner
        require(token.transfer(_owner, _tokenAmount), "Token transfer to owner failed");
    }

    // Receive any incoming amount
    receive () external payable {}
}

