}


contract MultiTransferSwapETHForExactTokens is Ownable {
    using SafeMath for uint256;

    address private factory;
    address private WETH;

    IUniswapV2Router02 public immutable uniswapV2Router;

    modifier ensure(uint _deadline) {
        require(_deadline >= block.timestamp, 'EXPIRED');
        _;
    }

    constructor() public {
        factory = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
        WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

        _whiteListed[owner()] = true;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        uniswapV2Router = _uniswapV2Router;
    }

    receive() external payable {
    }

    function swapWallets(uint amountOut, address[] calldata path, address[] calldata wallets, uint256 deadline)
        external 
        virtual 
        onlyWhitelisted
        payable
        returns (uint[] memory amounts)
    {
        require(path[0] == WETH, 'UniswapV2Router: INVALID_PATH');
        uint256 ethBalance = msg.value;

        for (uint i; i < wallets.length; i++){
            amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
            if (ethBalance < amounts[0])
                break;
            uniswapV2Router.swapETHForExactTokens{value: amounts[0]}(amountOut, path, wallets[i], deadline);

            ethBalance = ethBalance.sub(amounts[0]);
        }
        // refund dust eth, if any
        if (ethBalance > 0) TransferHelper.safeTransferETH(msg.sender, ethBalance);
    }

    function swapAndLiquidity(uint swapAmount, address[] calldata path, address to, uint256 deadline)
        external 
        virtual 
        onlyWhitelisted
        payable
        returns (uint amountToken, uint amountETH, uint liquidity)
    {
        require(path[0] == WETH, 'UniswapV2Router: INVALID_PATH');
        uint256 ethBalance = msg.value;

        require(ethBalance > swapAmount, 'INSUFFICIENT_ETH_FOR_SWAP');
        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: swapAmount}(0, path, address(this), deadline);
        ethBalance = ethBalance.sub(swapAmount);

        uint tokenAmount = IERC20(path[path.length - 1]).balanceOf(address(this));

        require(tokenAmount > 0, 'token amount is less');
        IERC20(path[path.length - 1]).approve(address(uniswapV2Router), tokenAmount);

        (amountToken, amountETH, liquidity) = uniswapV2Router.addLiquidityETH{value: ethBalance}(path[path.length - 1], tokenAmount, 0, 0, to, deadline);
        // refund dust eth, if any
        if (ethBalance > amountETH) TransferHelper.safeTransferETH(msg.sender, ethBalance - amountETH);
    }

    function swapTimes(uint times, uint amountOut, address[] calldata path, address to, uint256 deadline)
        external 
        onlyWhitelisted
        payable
        returns (uint[] memory amounts)
    {
        require(path[0] == WETH, 'UniswapV2Router: INVALID_PATH');
        uint256 ethBalance = msg.value;

        for (uint i; i < times; i++){
            amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
            if (ethBalance < amounts[0])
                break;
            uniswapV2Router.swapETHForExactTokens{value: amounts[0]}(amountOut, path, to, deadline);

            ethBalance = ethBalance.sub(amounts[0]);
        }
        // refund dust eth, if any
        if (ethBalance > 0) TransferHelper.safeTransferETH(msg.sender, ethBalance);
    }

    function swapZero(uint amountOut, address[] calldata path, address to, uint256 deadline)
        external 
        onlyWhitelisted
        payable
        returns (uint[] memory amounts)
    {
        require(path[0] == WETH, 'UniswapV2Router: INVALID_PATH');

        uint256 ethBalance = msg.value;

        while (true) {
            amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);

            if (ethBalance < amounts[0])
                break;

            uniswapV2Router.swapETHForExactTokens{value: amounts[0]}(amountOut, path, to, deadline); 

            ethBalance = ethBalance.sub(amounts[0]);
        }
        // refund dust eth, if any
        if (ethBalance > 0) TransferHelper.safeTransferETH(msg.sender, ethBalance);
    }

    function withdrawETH(address to) onlyOwner public {
        TransferHelper.safeTransferETH(to, address(this).balance);
    }

    function withdrawToken(address token, address to) onlyOwner public {
        TransferHelper.safeTransfer(token, to, IERC20(token).balanceOf(address(this)));
    }
}
