pragma solidity >=0.5.10 <0.7.0;

contract ElectronicCertificate {
    string  public studentPersonalName;
    string  public studentDocumentNumber;
    string  public certifierSignatureOne;
    string  public certifierSignatureTwo;
    string  public electronicCertificateGraphicHash;

    constructor (string memory _studentPersonalName, string memory _studentDocumentNumber, string memory _certifierSignatureOne, string memory _certifierSignatureTwo, string memory _electronicCertificateGraphicHash) public {
        studentPersonalName = _studentPersonalName;
        studentDocumentNumber = _studentDocumentNumber;
        certifierSignatureOne = _certifierSignatureOne;
        certifierSignatureTwo = _certifierSignatureTwo;
        electronicCertificateGraphicHash = _electronicCertificateGraphicHash;
    }

    function getElectronicCertificateSummary() public view returns (
        string memory _studentPersonalName,
        string memory _studentDocumentNumber, 
        string memory _certifierSignatureOne,
        string memory _certifierSignatureTwo,
        string memory _electronicCertificateGraphicHash
    ) {
        return (
            studentPersonalName,
            studentDocumentNumber,
            certifierSignatureOne,
            certifierSignatureTwo,
            electronicCertificateGraphicHash
        );
    }
}
