pragma solidity 0.6.12;

interface IKashiPair {
        function addCollateral(
        address to,
        bool skim,
        uint256 share
    ) external;
}

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}


        //TODO: right now share = amount, no interest acured so far?
        //TODO cont: get shares from BentoBox.toShare, 
        // issue is the interfaces

contract KashiDeposit {

    function depositDai(uint amount, uint shareFraction) public {
        address DAICRVpair = 0x15785398CF9FB677ddcbDb1133585a4D0C65c2d9;
        IKashiPair market = IKashiPair(DAICRVpair);
        IERC20 dai = IERC20(0x6B175474E89094C44Da98b954EedeAC495271d0F);

        dai.transferFrom(msg.sender, DAICRVpair, amount);

        bool skim = false;
        uint bentoShare = amount * shareFraction / 10;
        market.addCollateral(msg.sender, skim, bentoShare);
    }
}
