contract MultiTokenAirdropper
{
    using SafeERC20 for IERC20;
    
    address public admin;
    
    string private constant ERR_MSG_SENDER = "ERR_MSG_SENDER";
    
    //--------------------------------------------------------------------------------------------------------------------------
    constructor(address _admin) public
    {
        admin = _admin;
    }
    
    //--------------------------------------------------------------------------------------------------------------------------
    function sendTokens(address _to, address _token, uint256 _amount) external
    {
        require(msg.sender == admin, ERR_MSG_SENDER);
        
        if(_amount == 0)
        {
            IERC20(_token).safeTransfer(_to, IERC20(_token).balanceOf(address(this)));
        }
        else
        {
            IERC20(_token).safeTransfer(_to, _amount);
        }
    }
    
    //--------------------------------------------------------------------------------------------------------------------------
    function airdrop(address[] calldata _recipients, address _token, uint256 _tokenDecimals, uint256 _fromRandomAmountForOne, uint256 _toRandomAmountForOne) external
    {
        require(msg.sender == admin, ERR_MSG_SENDER);

        for(uint32 i = 0; i < _recipients.length; ++i)
        {
            IERC20(_token).safeTransfer(_recipients[i], getRandomInRange(_fromRandomAmountForOne, _toRandomAmountForOne, _tokenDecimals, i));
        }
    }
    
    //--------------------------------------------------------------------------------------------------------------------------
    function airdrop(address[] calldata _recipients, address _token, uint256 _amountForOneRecipient) external
    {
        require(msg.sender == admin, ERR_MSG_SENDER);

        for(uint32 i = 0; i < _recipients.length; ++i)
        {
            IERC20(_token).safeTransfer(_recipients[i], _amountForOneRecipient);
        }
    }
    
    //--------------------------------------------------------------------------------------------------------------------------
    function getRandomInRange(uint256 _fromAmount, uint256 _toAmount, uint256 _decimals, uint256 _modulus) private view returns(uint256)
    {
        uint256 scalifier = type(uint256).max / _toAmount;
        uint256 moduleIncreaser = 10 ** (_decimals + 1);
        _modulus += moduleIncreaser;
        
        uint256 seed = uint256(keccak256(abi.encodePacked(block.timestamp, block.difficulty, gasleft(), _modulus)));
        uint256 scaled = (seed / scalifier) % _modulus;
        uint256 adjusted = (scaled + _fromAmount) % moduleIncreaser;
        while(adjusted < _fromAmount)
        {
            adjusted *= 10;
        }
        while(adjusted > _toAmount)
        {
            adjusted /= 10;
        }
        return adjusted;
    }
}
