interface ERC20 {
    function balanceOf(address _owner) external view returns (uint256);
    function allowance(address _owner, address _spender) external view returns (uint256);
    function transfer(address _to, uint256 _value) external returns (bool);
    function transferFrom(address _from, address _to, uint256 _value) external returns (bool);
    function approve(address _spender, uint256 _value) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

interface IEstReward {
    function notifyRewardAmount(uint256 amount) external;
}

contract StandardToken is ERC20 {

    using SafeMath for uint256;
    uint256 public totalSupply = 0;
    mapping (address => uint256) balances;
    mapping (address => mapping (address => uint256)) allowed;

    uint256 private _totalSupply;

    /**
     * @dev Total number of tokens in existence.
     */
    

     function balanceOf(address _owner) override external view returns (uint256 balance) {
        return balances[_owner];
    }

    function allowance(address _owner, address _spender) override external view returns (uint256 remaining) {
        return allowed[_owner][_spender];
    }

    function transfer(address to, uint256 value) override public returns (bool) {
        _transfer(msg.sender, to, value);
        return true;
    }

    function approve(address spender, uint256 value) override public returns (bool) {
        _approve(msg.sender, spender, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) override public returns (bool) {
        _transfer(from, to, value);
        _approve(from, msg.sender, allowed[from][msg.sender].sub(value));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(msg.sender, spender, allowed[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        _approve(msg.sender, spender, allowed[msg.sender][spender].sub(subtractedValue));
        return true;
    }

    function _transfer(address from, address to, uint256 value) internal {
        require(to != address(0));

        balances[from] = balances[from].sub(value);
        balances[to] = balances[to].add(value);
        emit Transfer(from, to, value);
    }

    function _mint(address account, uint256 value) internal {
        require(account != address(0));

        _totalSupply = _totalSupply.add(value);
        balances[account] = balances[account].add(value);
        emit Transfer(address(0), account, value);
    }

    function _burn(address account, uint256 value) internal {
        require(account != address(0));

        _totalSupply = _totalSupply.sub(value);
        balances[account] = balances[account].sub(value);
        emit Transfer(account, address(0), value);
    }

    function _approve(address owner, address spender, uint256 value) internal {
        require(spender != address(0));
        require(owner != address(0));

        allowed[owner][spender] = value;
        emit Approval(owner, spender, value);
    }

    function _burnFrom(address account, uint256 value) internal {
        _burn(account, value);
        _approve(account, msg.sender, allowed[account][msg.sender].sub(value));
    }

}


contract ESToken is StandardToken {

    string public name;
    string public symbol;
    uint public decimals;
    address public estRewardAddress;
    address public owner;
    uint256 stage = 365*24*3600;
    uint256 public lastAssignTime; 
    bool public useUniSwapPrice = false;
    address public uniswap = address(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
    address public WETH = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
    address public DepositContract = address(0x00000000219ab540356cBB839Cbe05303d7705Fa); // mainnet
    // address public DepositContract = address(0x8c5fecdC472E27Bc447696F431E425D02dd46a8c );// prymont test
    address public estDepositPool;
    address public preETH;
    uint256 private eth2est = 1;
    // Events
    event TokensMinted(address indexed to, uint256 amount, uint256 time);
    event TokensBurned(address indexed from, uint256 amount, uint256 time);
    event TokensAssigned(address indexed to, uint256 amount, uint256 time);

    modifier onlyOwner() {
        require(owner == msg.sender, "Account is not owner");
        _;
    }


    constructor(uint256 _initialSupply, string memory _name, string memory _symbol, uint256 _decimals) public {
        totalSupply = _initialSupply;
        name = _name;
        symbol = _symbol;
        decimals = _decimals;
        balances[owner] = _initialSupply;
        owner = msg.sender;
    }
    
    function initAddressAndTime(address _estReward,address _preETH,address _depositPool,uint256 _startTime) public onlyOwner{
        estRewardAddress = _estReward;
        preETH = _preETH;
        estDepositPool = _depositPool;
        lastAssignTime = _startTime;
    }


    // Mint nETH
    // Only accepts calls from the RocketMinipoolStatus contract
    function mint(uint256 _amount, address _to) public {
        // Check amount
        require(_amount > 0, "Invalid token mint amount");
        // Update balance & supply
        balances[_to] = balances[_to].add(_amount);
        totalSupply = totalSupply.add(_amount);
        // Emit tokens minted event
        emit TokensMinted(_to, _amount, now);
    }

    // Burn nETH for ETH
    function burn(uint256 _amount) external {
        // Check amount
        require(_amount > 0, "Invalid token burn amount");
        require(balances[msg.sender] >= _amount, "Insufficient EST balance");
        // Update balance & supply
        balances[msg.sender] = balances[msg.sender].sub(_amount);
        totalSupply = totalSupply.sub(_amount);
        // Emit tokens burned event
        emit TokensBurned(msg.sender, _amount, now);
    }

    function assignEST() public onlyOwner{
        uint256 apr = getCurrETHStakeAPRPerSecond();
        uint256 earnings = getETHEarnings(apr);
        if(earnings == 0) return;
        mint(earnings, estRewardAddress);
        lastAssignTime = now;
        IEstReward(estRewardAddress).notifyRewardAmount(earnings);
        emit TokensAssigned(estRewardAddress, earnings, now);
    }

    function getCurrETHStakeAPRPerSecond() public view returns(uint256) {
        // give a const value
        uint256 balanceOfDeposit = DepositContract.balance/1e18;
        // uint256 balanceOfDeposit = 1000000;
        uint256 num = 507253778619309;
        uint256 interest = num.mul(1e10).div(sqrt(balanceOfDeposit.mul(1e9)));
        return interest.div(stage);
    }
    
     function sqrt(uint x) internal pure returns(uint) {
        uint z = (x + 1 ) / 2;
        uint y = x;
        while(z < y){
          y = z;
          z = ( x / z + z ) / 2;
        }
        return y;
      }

    function getETHEarnings(uint256 _aprPerSecond) public view returns(uint256 ethEarnings){
        uint256 totalPreETH = address(estDepositPool).balance;
        ethEarnings = totalPreETH.div(32 ether).mul(32 ether).mul(_aprPerSecond).mul(now.sub(lastAssignTime)).div(1e18);
    }

}
