pragma solidity ^0.8.2;

contract SingleUseNonce {
    mapping(address => mapping(uint256 => bool)) _usedNonces;

    modifier unusedNonce(uint256 nonce) {
        require(!_usedNonces[msg.sender][nonce]);
        _;
        _usedNonces[msg.sender][nonce] = true;
    }
}

