// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.2;

library TicketLib {
    function splitTicket(bytes memory ticket)
    private
    pure
    returns (
        bytes32 r,
        bytes32 s,
        uint8 v
    )
    {
        require(ticket.length == 65);

        assembly {
        /*
        First 32 bytes stores the length of the signature
        add(sig, 32) = pointer of sig + 32
        effectively, skips first 32 bytes of signature
        mload(p) loads next 32 bytes starting at the memory address p into memory
        */

        // first 32 bytes, after the length prefix
            r := mload(add(ticket, 32))
        // second 32 bytes
            s := mload(add(ticket, 64))
        // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(ticket, 96)))
        }

        // implicitly return (r, s, v)
    }

    function recoverSigner(bytes32 signedTicketHash, bytes memory ticket)
    private
    pure
    returns (address)
    {
        (bytes32 r, bytes32 s, uint8 v) = splitTicket(ticket);

        return ecrecover(signedTicketHash, v, r, s);
    }

    function getMessage(bytes32 messageHash)
    private
    pure
    returns (bytes32)
    {
        return
        keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
        );
    }

    function getMessageHash(
        address minter,
        uint256 nonce
    ) private pure returns (bytes32) {
        return keccak256(abi.encodePacked(minter, nonce));
    }

    function verify(address signer, uint256 nonce, bytes calldata ticket) public view returns (bool) {
        bytes32 ticketHash = getMessageHash(msg.sender, nonce);
        bytes32 signedTicketHash = getMessage(ticketHash);

        return recoverSigner(signedTicketHash, ticket) == signer;
    }
}

