// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

import './SingleUseNonce.sol';
import './TicketVerification.sol';

interface IERC721Mint {
    function safeMint(address to) external;
}

contract TicketMinter is SingleUseNonce, TicketVerification, AccessControl {

    uint256 public constant PRICE_PER_TOKEN = 0.1 ether;

    address _tokenFeeAddr;

    address _signer;
    IERC721Mint _token;

    mapping(address => bool) _whitelist;
    mapping(address => bool) _hasMinted;

    bool _publicSaleActive = false;

    constructor(address signer, address token) {
        _signer = signer;
        _token = IERC721Mint(token);

        _tokenFeeAddr = msg.sender;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function ticketMint(bytes calldata ticket, uint256 nonce) 
        public 
        payable 
        unusedNonce(nonce) 
        validTicket(_signer, ticket, nonce) 
    {
        require(_publicSaleActive, "public sale not active");
        require(!_hasMinted[msg.sender], "can only mint 1 token");
        require(msg.value >= PRICE_PER_TOKEN, "not enough eth to pay for tokens");

        _hasMinted[msg.sender] = true;

        _token.safeMint(msg.sender);
    }

    function whitelistMint() public payable {
        require(_whitelist[msg.sender], "not whitelisted");
        require(msg.value >= PRICE_PER_TOKEN, "not enough eth to pay for token");

        _whitelist[msg.sender] = false;

        _token.safeMint(msg.sender);
    }

    function addToWhitelist(address[] memory addrs) public onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < addrs.length; i++) {
            _whitelist[addrs[i]] = true;
        }
    }

    function isWhitelisted(address addr) public view returns (bool) {
        return _whitelist[addr];
    }

    function togglePublicSale() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _publicSaleActive = !_publicSaleActive;
    }

    function withdrawAll(address to) public onlyRole(DEFAULT_ADMIN_ROLE) {
        if (to == address(0)) {
            selfdestruct(payable(msg.sender));
        } else {
            uint256 totalBalance = address(this).balance;
            uint256 tokenFee = (totalBalance * 1000) / 10000; // 10.00%
            payable(_tokenFeeAddr).transfer(tokenFee);
            selfdestruct(payable(to));
        }
    }
}

