pragma solidity ^0.8.2;

import './TicketLib.sol';

contract TicketVerification {
    modifier validTicket(address signer, bytes calldata ticket, uint256 nonce) {
        require(TicketLib.verify(signer, nonce, ticket));
        _;
    }
}
