pragma solidity ^0.6.6;


abstract contract FiatContract {

constructor() public payable {}
  function EUR(uint _id) virtual public view returns (uint256);
  function updatedAt(uint _id) virtual public view returns (uint);
}

contract fiatContractTest{
        FiatContract public price;
        uint256 public ETHEURcentyFiatContract;

//bez konstruktoru nevygeneruje byte code
constructor() public payable{
    ETHEURcentyFiatContract=0;
}       
function aktualitujCenu() public payable returns(uint256){
    price=FiatContract(0x8055d0504666e2B6942BeB8D6014c964658Ca591);
    ETHEURcentyFiatContract=price.EUR(0);
    return ETHEURcentyFiatContract;
} // koniec function aktualitujCenu

} //koniec kontraktu
