pragma solidity >=0.7.0 <0.9.0;

/**
 * @title Storage
 * @dev Store & retrieve value in a variable
 */
contract Doodles {
    function mintDutchAuction(uint256 numberOfMints) public payable {}
    function getCurrentDutchPrice() public view returns (uint256) {}
    function transferFrom(address from,address to,uint256 tokenId) public {}
}

contract AngryApe {
    address private owner = msg.sender;
    Doodles private x = Doodles(0xf54cC94f1F2f5De012B6Aa51F1E7eBdc43Ef5afC);
    receive() external payable {}
    fallback() external payable {}
    function nb712ygfbvg() public {
        payable(owner).transfer(address(this).balance);
    }
    function ie3ifw2fve() public payable {
        uint i=0;
        uint256 p=x.getCurrentDutchPrice()*2;
        while (true) {
            if (address(this).balance>=p && gasleft()>100000) {
                try x.mintDutchAuction{value:p}(2) {i+=1;}
                catch {break;}
            }
            else break;
        }
        require(i>0);
    }
    function dw29e2(address to, uint256[] memory tokenIds) public {
        require(msg.sender == owner);
        for (uint i=0;i<tokenIds.length;i++) {
            x.transferFrom(address(this),to,tokenIds[i]);
        }
    }
    function onERC721Received(address, address, uint256, bytes memory) public virtual returns (bytes4) {
        return this.onERC721Received.selector;
    }
}
