contract StoreBase is ERC721Holder, OperatorAccess, Pausable {
    event AuctionCancelled(uint256 _tokenId);
    bytes4 public constant INTERFACE_ID_ERC721 = 0x80ac58cd;
    IERCCore public nftAddress;
    IERC2612 public paymentToken;

    uint256 public cut;
    address public officialAddr;

    constructor(
        IERCCore _nftAddress,
        IERC2612 _paymentToken,
        uint256 _cut,
        address _officialAddr
    ) public {
        require(
            _nftAddress.supportsInterface(INTERFACE_ID_ERC721),
            "is not 721 token"
        );
        nftAddress = _nftAddress;
        _setupRole(OPERATOR_ROLE, address(_nftAddress));
        paymentToken = _paymentToken;
        cut = _cut;
        officialAddr = _officialAddr;
    }

    function setNftAddress(IERCCore _newNftAddress) external onlyOperator{
        nftAddress = _newNftAddress;
    }

    function setPaymentToken(IERC2612 _newPaymentToken) external onlyOperator{
        paymentToken = _newPaymentToken;
    }

    function setCut(uint256 _cut) external onlyOperator{
        cut = _cut;
    }

    function setOfficialAddr(address _officialAddr) external onlyOperator{
        officialAddr = _officialAddr;
    }

    function _escrow(address _client, uint256 _tokenId) internal {
        nftAddress.safeTransferFrom(_client, address(this), _tokenId);
    }

    function _transfer(address _receiver, uint256 _tokenId) internal {
        nftAddress.safeTransferFrom(address(this), _receiver, _tokenId);
    }

    /// @dev Computes owner's cut of a sale.
    /// @param _price - Sale price of NFT.
    function _computeCut(uint256 _price) internal view returns (uint256) {
        return (_price * cut) / 10000;
    }
}

