contract LecubeCopyright is StoreBase {
    mapping(uint256 => Copyright) public tokenIdToCopyright;
    struct Copyright {
        // Current owner of NFT
        address owner;
        // Price (in wei) at this works to copy
        uint256 price;
        uint256 startedAt;
    }
    event CopyrightCreated(uint256 tokenId, uint256 price);

    event CopyrightCancelled(uint256 tokenId);
    event CopySuccessful(
        uint256 tokenId,
        uint256 price,
        uint256 num,
        address buyer
    );

    constructor(
        IERCCore _nftAddress,
        IERC2612 _paymentToken,
        uint256 _cut,
        address _officialAddr
    ) public StoreBase(_nftAddress, _paymentToken, _cut, _officialAddr) {}

    function _addCopyright(uint256 _tokenId, Copyright memory _copyright) internal {
        tokenIdToCopyright[_tokenId] = _copyright;

        emit CopyrightCreated(uint256(_tokenId), uint256(_copyright.price));
    }

    /// @dev Cancels an copyright unconditionally.
    function _cancelCopyright(uint256 _tokenId, address _owner) internal {
        _removeCopyright(_tokenId);
        _transfer(_owner, _tokenId);
        emit CopyrightCancelled(_tokenId);
    }

    /// @dev Returns true if the NFT is on copyright.
    /// @param _copyright - Copyright to check.
    function _isOnCopyright(Copyright storage _copyright)
        internal
        view
        returns (bool)
    {
        return (_copyright.startedAt > 0);
    }

    /// @dev Removes an copyright from the list of open copyrights.
    /// @param _tokenId - ID of NFT on copyright.
    function _removeCopyright(uint256 _tokenId) internal {
        delete tokenIdToCopyright[_tokenId];
    }

    /// @dev Creates and begins a new copyright.
    /// @param _tokenId - ID of token to copyright, sender must be owner.
    /// @param _price - Price of item (in wei) for copy this works
    /// @param _owner - Owner, if not the message sender
    function createOrder(
        uint256 _tokenId,
        uint256 _price,
        address _owner
    ) external whenNotPaused onlyOperator returns (bool) {
        _escrow(_owner, _tokenId);
        Copyright memory copyright = Copyright(
            _owner,
            _price,
            uint256(block.timestamp)
        );
        _addCopyright(_tokenId, copyright);
    }

    //copy this token with ltc
    function copy(uint256 tokenId, uint256 num)
        external
        whenNotPaused
        returns (bool)
    {
        require(num > 0, "num error");
        require(num < 100, "num too large");
        Copyright storage copyright = tokenIdToCopyright[tokenId];

        require(_isOnCopyright(copyright));

        uint256 price = copyright.price;

        address owner = copyright.owner;

        // Transfer proceeds to seller (if there are any!)
        if (price > 0) {
            uint256 copyCut = _computeCut(price);
            uint256 ownerProceeds = price - copyCut;

            paymentToken.transferFrom(msg.sender, address(this), price * num);
            paymentToken.transfer(officialAddr, copyCut * num);
            paymentToken.transfer(owner, ownerProceeds * num);
        }
        IERCCore(nftAddress).IncrementCopy(msg.sender, tokenId, num);
        // emit the event CopySuccessful
        emit CopySuccessful(tokenId, price, num, msg.sender);
        return true;
    }

    function copy(
        uint256 tokenId,
        uint256 num,
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external whenNotPaused returns (bool) {
        require(num > 0, "num error");
        require(num < 100, "num too large");
        Copyright storage copyright = tokenIdToCopyright[tokenId];

        require(_isOnCopyright(copyright));

        uint256 price = copyright.price;

         require(amount >= price * num, "insufficient allowance");

        address owner = copyright.owner;

        // Transfer proceeds to seller (if there are any!)
        if (price > 0) {
            uint256 copyCut = _computeCut(price);
            uint256 ownerProceeds = price - copyCut;
            paymentToken.permit(
                msg.sender,
                address(this),
                amount,
                deadline,
                v,
                r,
                s
            );
            paymentToken.transferFrom(msg.sender, address(this), price * num);
            paymentToken.transfer(officialAddr, copyCut * num);
