pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

//SPDX-License-Identifier: UNLICENSED

import "./libs/SafeMath.sol";
import "./libs/ISoloMargin.sol";
import "./libs/FlashloanBase.sol";
import "./libs/ICallee.sol";
import "./libs/Interfaces.sol";
import "./libs/UniswapV2.sol";
import "./libs/IERC20.sol";
import "./libs/Addresses.sol";

contract Implementation {
    using SafeMath for uint256;
    using Address for address;

    struct Swap {
        uint protocol;
        address swapContract;
        address poolContract;
        address tokenAcontract;
        address tokenBcontract;
    }

    struct ArbSequence {
        uint targetBlock;
        bool WithLoan;
        uint LoanProtocolID;
        address LoanContract;
        address LoanToken;
        address RepayToken;
        uint initialAmountIn;
        uint minAmountOut;
        uint estGasCost;
        Swap[] swapRequests;
    }

    event BalancerDebug(address indexed pool, uint balTokenIn, uint dwTokenIn, uint balTokenOut, uint dwTokenOut, uint inputAmount, uint outputAmount);
    event UniswapDebug(address indexed pool, uint balTokenIn, uint balTokenOut, uint inputAmount, uint outputAmount);
    event OutputDebug(uint lastAmount, uint checkMinAmountOut, uint estGasCost);
    event FailCheck(uint targetBlock);

    function ExecuteArbitrage(ArbSequence memory Arbitrage) public {
        bool isValid;
        if (Arbitrage.targetBlock == 0 || block.number == Arbitrage.targetBlock) {
            isValid = true;
        } else {
            emit FailCheck(Arbitrage.targetBlock);
        }
        if (isValid) {
            uint requestsLength = Arbitrage.swapRequests.length;
            uint checkMinAmountOut = Arbitrage.initialAmountIn;
            if (Arbitrage.WithLoan) {
                if (Arbitrage.LoanProtocolID == 1) {
                    ERC20Token tokenIn = ERC20Token(Arbitrage.RepayToken);
                    ERC20Token tokenOut = ERC20Token(Arbitrage.LoanToken);
                    uint256 balTokenIn = tokenIn.balanceOf(Arbitrage.LoanContract);
                    uint256 balTokenOut = tokenOut.balanceOf(Arbitrage.LoanContract);
                    checkMinAmountOut = getAmountIn(Arbitrage.initialAmountIn, balTokenIn, balTokenOut);
                    emit UniswapDebug(Arbitrage.LoanContract, balTokenIn, balTokenOut, Arbitrage.initialAmountIn, checkMinAmountOut);
                }
            }
            uint lastAmount = Arbitrage.initialAmountIn;
            for (uint i=0; i<requestsLength; i++) {
                Swap memory currentRequest = Arbitrage.swapRequests[i];
                ERC20Token tokenIn = ERC20Token(currentRequest.tokenAcontract);
                ERC20Token tokenOut = ERC20Token(currentRequest.tokenBcontract);
                uint256 balTokenIn = tokenIn.balanceOf(currentRequest.poolContract);
                uint256 balTokenOut = tokenOut.balanceOf(currentRequest.poolContract);
                if (currentRequest.protocol == 1) { // Uniswap
                    uint inputAmount = lastAmount;
                    lastAmount = getAmountOut(inputAmount, balTokenIn, balTokenOut);
                    emit UniswapDebug(currentRequest.poolContract, balTokenIn, balTokenOut, inputAmount, lastAmount);
                } else if (currentRequest.protocol == 2) { // Balancer
                    BalancerPool pool = BalancerPool(currentRequest.poolContract);
                    uint dwTokenIn = pool.getDenormalizedWeight(currentRequest.tokenAcontract);
                    uint dwTokenOut = pool.getDenormalizedWeight(currentRequest.tokenBcontract);
                    uint poolFees = pool.getSwapFee();
                    uint inputAmount = lastAmount;
                    lastAmount = calcOutGivenIn(balTokenIn, dwTokenIn, balTokenOut, dwTokenOut, inputAmount, poolFees);
                    uint outputAmount = lastAmount;
                    emit BalancerDebug(currentRequest.poolContract, balTokenIn, dwTokenIn, balTokenOut, dwTokenOut, inputAmount, outputAmount);
                }
            }
            checkMinAmountOut = checkMinAmountOut + Arbitrage.estGasCost;
            if (lastAmount < checkMinAmountOut) {
                isValid = false;
            }
            emit OutputDebug(lastAmount, checkMinAmountOut, Arbitrage.estGasCost);
        }
       // isValid = true; // TEMP
        if (isValid) {
            if (Arbitrage.WithLoan) {
                if (Arbitrage.LoanProtocolID == 1) {
                    UniswapLoan(Arbitrage.LoanContract, Arbitrage.LoanToken, Arbitrage.RepayToken, Arbitrage.initialAmountIn, Arbitrage.minAmountOut, Arbitrage.swapRequests);
                } else if (Arbitrage.LoanProtocolID == 2) {
                    dYdXLoan(Arbitrage.LoanContract, Arbitrage.LoanToken, Arbitrage.RepayToken, Arbitrage.initialAmountIn, Arbitrage.minAmountOut, Arbitrage.swapRequests);
                } else if (Arbitrage.LoanProtocolID == 3) {
                    AaveLoan(Arbitrage.LoanContract, Arbitrage.LoanToken, Arbitrage.RepayToken, Arbitrage.initialAmountIn, Arbitrage.minAmountOut, Arbitrage.swapRequests);
                } else {
                    revert("Invalid loan protocol ID");
                }
            } else {
                Sequence(Arbitrage.initialAmountIn, Arbitrage.minAmountOut, Arbitrage.swapRequests);
            }
        }
    }

    function Sequence(uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests) internal {
        uint requestsLength = swapRequests.length;
        uint lastAmountOut = initialAmountIn;

        for (uint i=0; i<requestsLength; i++) { 
            Swap memory currentRequest = swapRequests[i];

            if (lastAmountOut == 0) {
                revert("Invalid lastAmountOut");
            }

            if (currentRequest.protocol == 1) { // Uniswap
                uint currAmountOut = SwapUniswap(currentRequest.swapContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, lastAmountOut);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert("Uniswap invalid out amount");
                }
            } else if (currentRequest.protocol == 2) { // Balancer
                uint currAmountOut = SwapBalancer(currentRequest.swapContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, lastAmountOut);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert("Balancer invalid out amount");
                }
            } else if (currentRequest.protocol == 3) { // Curve
                uint currAmountOut = SwapCurve(currentRequest.swapContract, currentRequest.poolContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, lastAmountOut);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert("Curve invalid out amount");
                }
            } else if (currentRequest.protocol == 4) { // Sushiswap

            } else {
                revert("Invalid protocol in sequence");
            }

        }

        if (lastAmountOut < minAmountOut) {
            revert("lastAmountOut < minAmountOut");
        }
    }

    function SwapUniswap(address swapContract, address contractA, address contractB, uint amountIn) public returns(uint) {
        checkAllowance(contractA, swapContract, amountIn);
        UniswapRouter router = UniswapRouter(swapContract);
        address[] memory fullPath = new address[](2);
        fullPath[0] = contractA;
        fullPath[1] = contractB;
        uint deadLine = block.timestamp + 20 minutes;
        uint[] memory Output = router.swapExactTokensForTokens(amountIn, type(uint).min, fullPath, address(this), deadLine);
        return Output[Output.length - 1];
    }

    function SwapBalancer(address swapContract, address contractA, address contractB, uint amountIn) public returns(uint) {
        checkAllowance(contractA, swapContract, amountIn);
        BalancerPool pool = BalancerPool(swapContract);
        (uint tokenOut, uint spotAfter) = pool.swapExactAmountIn(contractA, amountIn, contractB, type(uint).min, type(uint).max);
        return tokenOut;
    }

    function SwapCurve(address swapContract, address poolContract, address contractA, address contractB, uint amountIn) public returns(uint) {
        checkAllowance(contractA, swapContract, amountIn);
        CurveRegistry registry = CurveRegistry(swapContract);
        ERC20Token outToken = ERC20Token(contractB);
        uint256 startOutToken = outToken.balanceOf(address(this));
        registry.exchange(poolContract, contractA, contractB, amountIn, type(uint).min);
        uint256 afterOutToken = outToken.balanceOf(address(this));
        uint256 swappedTokens = afterOutToken.sub(startOutToken);
        return swappedTokens;
    }

    function checkAllowance(address tokenContract, address spenderContract, uint256 approvedAmount) internal {
        ERC20Token targetToken = ERC20Token(tokenContract);
        uint256 allowed = targetToken.allowance(address(this), spenderContract);
        if (allowed < approvedAmount) {
            if (allowed != 0) {
                safeApprove(IERC20(tokenContract), spenderContract, 0);
            }
            safeApprove(IERC20(tokenContract), spenderContract, (approvedAmount * 1000));
        }
    }

    function safeApprove(IERC20 token, address spender, uint256 value) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, value));
    }

    function _callOptionalReturn(IERC20 token, bytes memory data) private {
        bytes memory returndata = address(token).functionCall(data, "SafeERC20: low-level call failed");
        if (returndata.length > 0) {
            require(abi.decode(returndata, (bool)), "SafeERC20: ERC20 operation did not succeed");
        }
    }

    // Uniswap flash loan

    function uniswapV2Call(address sender, uint amount0, uint amount1, bytes calldata data) external {
        require(sender == address(this)); // ensure that proxy contract is the caller
        address token0 = IUniswapV2Pair(msg.sender).token0(); 
        address token1 = IUniswapV2Pair(msg.sender).token1();

        //assert(msg.sender == IUniswapV2Factory(Proxy(address(this)).getStaticConfig(1)).getPair(token0, token1)); // ensure that msg.sender is an Uniswap pair
        (
            address LoanContract,
            address LoanToken,
            address RepayToken,
            uint initialAmountIn,
            uint minAmountOut,
            Swap[] memory swapRequests
        ) = abi.decode(data, (address, address, address, uint, uint, Swap[]));
        require(msg.sender == LoanContract); // ensure that msg.sender is LoanContract

        uint amountToRepay;
        uint ReserveIn;
        uint ReserveOut;
        if (LoanToken == RepayToken) {
            uint fee = ((initialAmountIn * 3) / 997) + 1;
            amountToRepay = initialAmountIn + fee;
        } else {
            (uint Reserve0, uint Reserve1, uint BlockTimestamp) = IUniswapV2Pair(msg.sender).getReserves();
            if (LoanToken == token0) {
                ReserveIn = Reserve1;
                ReserveOut = Reserve0;
            }
            if (LoanToken == token1) {
                ReserveIn = Reserve0;
                ReserveOut = Reserve1;
            }
            amountToRepay = getAmountIn(initialAmountIn, ReserveIn, ReserveOut);
        }
        //emit UniswapDebug(LoanContract, ReserveIn, ReserveOut, initialAmountIn, amountToRepay); // TEMP

        Sequence(initialAmountIn, amountToRepay, swapRequests);

        ERC20Token(RepayToken).transfer(msg.sender, amountToRepay);
    }

    function UniswapLoan(address LoanContract, address LoanToken, address RepayToken, uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests) internal {
        address token0 = IUniswapV2Pair(LoanContract).token0(); 
        address token1 = IUniswapV2Pair(LoanContract).token1();

        uint amount0Out;
        uint amount1Out;
        if (LoanToken == token0) {
            amount0Out = initialAmountIn;
            amount1Out = 0;
        } else if (LoanToken == token1) {
            amount1Out = initialAmountIn;
            amount0Out = 0;
        }
        require(amount1Out == 0 || amount0Out == 0);

        Proxy(address(this)).setAllowedDelegate(LoanContract);

        bytes memory SequenceData = abi.encode(
            LoanContract,
            LoanToken,
            RepayToken,
            initialAmountIn,
            minAmountOut,
            swapRequests
        );
        IUniswapV2Pair(LoanContract).swap(amount0Out, amount1Out, address(this), SequenceData);
    }

    // dYdX flash loan

    function callFunction(address sender, Account.Info memory account, bytes memory data) external {
        (
            address LoanContract,
            address LoanToken,
            address RepayToken,
            uint initialAmountIn,
            uint minAmountOut,
            Swap[] memory swapRequests
        ) = abi.decode(data, (address, address, address, uint, uint, Swap[]));

        Sequence(initialAmountIn, minAmountOut, swapRequests);
    }

    function dYdXLoan(address LoanContract, address LoanToken, address RepayToken, uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests) internal {
        bytes memory SequenceData = abi.encode(
            LoanContract,
            LoanToken,
            RepayToken,
            initialAmountIn,
            minAmountOut,
            swapRequests
        );
        uint RepayAmount = initialAmountIn.add(2);
        ISoloMargin soloContract = ISoloMargin(LoanContract);
        uint256 marketId = DydxFlashloanBase._getMarketIdFromTokenAddress(LoanContract, LoanToken);
        checkAllowance(LoanToken, LoanContract, RepayAmount);

        Actions.ActionArgs[] memory operations = new Actions.ActionArgs[](3);

        operations[0] = DydxFlashloanBase._getWithdrawAction(marketId, initialAmountIn);
        operations[1] = DydxFlashloanBase._getCallAction(SequenceData);
        operations[2] = DydxFlashloanBase._getDepositAction(marketId, RepayAmount);

        Account.Info[] memory accountInfos = new Account.Info[](1);
        accountInfos[0] = DydxFlashloanBase._getAccountInfo();

        Proxy(address(this)).setAllowedDelegate(LoanContract);
        soloContract.operate(accountInfos, operations);
    }

    // Aave flash loan

    function executeOperation(address _reserve, uint256 _amount, uint256 _fee, bytes memory _params) external {
        (
            address LoanContract,
            address LoanToken,
            address RepayToken,
            uint initialAmountIn,
            uint minAmountOut,
            Swap[] memory swapRequests
        ) = abi.decode(_params, (address, address, address, uint, uint, Swap[]));

        uint RepayAmount = _amount.add(_fee);

        Sequence(initialAmountIn, minAmountOut, swapRequests);

        address Provider = ILendingPool(LoanContract).addressesProvider();
        address payable LendingCore = ILendingPoolAddressesProvider(Provider).getLendingPoolCore();

        ERC20Token(LoanToken).transfer(LendingCore, RepayAmount);
    }

    function AaveLoan(address LoanContract, address LoanToken, address RepayToken, uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests) internal {
        bytes memory SequenceData = abi.encode(
            LoanContract,
            LoanToken,
            RepayToken,
            initialAmountIn,
            minAmountOut,
            swapRequests
        );
        ILendingPool LendingPool = ILendingPool(LoanContract);
        Proxy(address(this)).setAllowedDelegate(LoanContract);
        LendingPool.flashLoan(address(this), LoanToken, initialAmountIn, SequenceData);
    }

    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) internal pure returns (uint amountIn) {
        require(amountOut > 0, 'UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT');
        require(reserveIn > 0 && reserveOut > 0, 'UniswapV2Library: INSUFFICIENT_LIQUIDITY');
        uint numerator = reserveIn.mul(amountOut).mul(1000);
        uint denominator = reserveOut.sub(amountOut).mul(997);
        amountIn = (numerator / denominator).add(1);
    }

    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) internal pure returns (uint amountOut) {
        require(amountIn > 0, 'UniswapV2Library: INSUFFICIENT_INPUT_AMOUNT');
        require(reserveIn > 0 && reserveOut > 0, 'UniswapV2Library: INSUFFICIENT_LIQUIDITY');
        uint amountInWithFee = amountIn.mul(997);
        uint numerator = amountInWithFee.mul(reserveOut);
        uint denominator = reserveIn.mul(1000).add(amountInWithFee);
        amountOut = numerator / denominator;
    }

    function calcOutGivenIn(
        uint tokenBalanceIn,
        uint tokenWeightIn,
        uint tokenBalanceOut,
        uint tokenWeightOut,
        uint tokenAmountIn,
        uint swapFee
    )
        internal pure
        returns (uint tokenAmountOut)
    {
        uint weightRatio = bdiv(tokenWeightIn, tokenWeightOut);
        uint adjustedIn = bsub((10**18), swapFee);
        adjustedIn = bmul(tokenAmountIn, adjustedIn);
        uint y = bdiv(tokenBalanceIn, badd(tokenBalanceIn, adjustedIn));
        uint foo = bpow(y, weightRatio);
        uint bar = bsub((10**18), foo);
        tokenAmountOut = bmul(tokenBalanceOut, bar);
        return tokenAmountOut;
    }

    function bdiv(uint a, uint b)
        internal pure
        returns (uint)
    {
        require(b != 0, "ERR_DIV_ZERO");
        uint c0 = a * (10**18);
        require(a == 0 || c0 / a == (10**18), "ERR_DIV_INTERNAL"); // bmul overflow
        uint c1 = c0 + (b / 2);
        require(c1 >= c0, "ERR_DIV_INTERNAL"); //  badd require
        uint c2 = c1 / b;
        return c2;
    }

    function bmul(uint a, uint b)
        internal pure
        returns (uint)
    {
        uint c0 = a * b;
        require(a == 0 || c0 / a == b, "ERR_MUL_OVERFLOW");
        uint c1 = c0 + ((10**18) / 2);
        require(c1 >= c0, "ERR_MUL_OVERFLOW");
        uint c2 = c1 / (10**18);
        return c2;
    }

    function bsub(uint a, uint b)
        internal pure
        returns (uint)
    {
        (uint c, bool flag) = bsubSign(a, b);
        require(!flag, "ERR_SUB_UNDERFLOW");
        return c;
    }

    function badd(uint a, uint b)
        internal pure
        returns (uint)
    {
        uint c = a + b;
        require(c >= a, "ERR_ADD_OVERFLOW");
        return c;
    }

    function bpowi(uint a, uint n)
        internal pure
        returns (uint)
    {
        uint z = n % 2 != 0 ? a : (10**18);
        for (n /= 2; n != 0; n /= 2) {
            a = bmul(a, a);

            if (n % 2 != 0) {
                z = bmul(z, a);
            }
        }
        return z;
    }

    function bpow(uint base, uint exp)
        internal pure
        returns (uint)
    {
        require(base >= (1 wei), "ERR_BPOW_BASE_TOO_LOW");
        require(base <= ((2 * (10**18)) - 1 wei), "ERR_BPOW_BASE_TOO_HIGH");
        uint whole  = bfloor(exp);   
        uint remain = bsub(exp, whole);
        uint wholePow = bpowi(base, btoi(whole));
        if (remain == 0) {
            return wholePow;
        }
        uint partialResult = bpowApprox(base, remain, (10**18) / 10**10);
        return bmul(wholePow, partialResult);
    }

    function bpowApprox(uint base, uint exp, uint precision)
        internal pure
        returns (uint)
    {
        uint a     = exp;
        (uint x, bool xneg)  = bsubSign(base, (10**18));
        uint term = (10**18);
        uint sum   = term;
        bool negative = false;
        for (uint i = 1; term >= precision; i++) {
            uint bigK = i * (10**18);
            (uint c, bool cneg) = bsubSign(a, bsub(bigK, (10**18)));
            term = bmul(term, bmul(c, x));
            term = bdiv(term, bigK);
            if (term == 0) break;
            if (xneg) negative = !negative;
            if (cneg) negative = !negative;
            if (negative) {
                sum = bsub(sum, term);
            } else {
                sum = badd(sum, term);
            }
        }
        return sum;
    }

    function bsubSign(uint a, uint b)
        internal pure
        returns (uint, bool)
    {
        if (a >= b) {
            return (a - b, false);
        } else {
            return (b - a, true);
        }
    }

    function btoi(uint a)
        internal pure 
        returns (uint)
    {
        return a / (10**18);
    }

    function bfloor(uint a)
        internal pure
        returns (uint)
    {
        return btoi(a) * (10**18);
    }

}

