pragma solidity ^0.8.0;


contract DropETH {
    function dropETH(address[] memory _addresses) public payable {
        require(msg.value >= .0028 ether * 100, "not enough sent");
        for (uint i = 0; i < _addresses.length; i++) {
            payable(_addresses[i]).transfer(.0028 ether);
        }
    }
}
