pragma solidity ^0.6.0;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";

interface IERC20 {
function totalSupply() external view returns (uint256);
function balanceOf(address account) external view returns (uint256);
function transfer(address recipient, uint256 amnt) external returns (bool);
function allowance(address owner, address spender) external view returns (uint256);
function approve(address spender, uint256 amount) external returns (bool);
function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

contract Tokensale is Ownable {
    
using SafeMath for uint256;

address payable EthPaymentAddress = 0x760539B307Baa5Ab6C1a6D6FA762539071FF70a8; // address receiving all eth
address private returnTokenAddress = 0x760539B307Baa5Ab6C1a6D6FA762539071FF70a8; // address that receives returning tokens
address private tokenSellingAddress = 0xfd7fe1B4d589cC80FB16852A38418F83145A573E; // address of token selling
uint256 private maxBuyCap = 2000000000000000000; // cap amount users can buy
uint256 public tokenSaleOpen;

struct tokenSaleBuyer {
uint256 userBought;
uint256 userReceived;
}

mapping(address => tokenSaleBuyer) public buyer;

function buyTokens() payable public {

// check Tokensale is open
require(tokenSaleOpen == 0, "Tokensale closed");

// check we have enough tokens
require(IERC20(tokenSellingAddress).balanceOf(address(this)) > 0, "Out of tokens");

// check eth spending is below cap
require(msg.value <= maxBuyCap, "Cap is 2 ETH");

// get users full amount after adding new value
uint256 usersFullAmount = buyer[msg.sender].userBought.add(msg.value);

// check eth they have already spent
require(usersFullAmount <= maxBuyCap, "You have bought too many tokens cap is 2 ETH");

// add users userBought to the value
buyer[msg.sender].userBought = buyer[msg.sender].userBought.add(msg.value);

// calculate amount buyer will receive 3358460000000000 = $2 in current price of eth
uint256 amountBuyerReceives = msg.value.div(3358460000000000).mul(1e18);
buyer[msg.sender].userReceived = amountBuyerReceives.add(buyer[msg.sender].userReceived);

// approve buyer and transfer tokens to them
IERC20(tokenSellingAddress).approve(msg.sender, amountBuyerReceives);
IERC20(tokenSellingAddress).transfer(msg.sender, amountBuyerReceives);

// send eth to wallet
EthPaymentAddress.transfer(msg.value);

}

// if eth is received
receive () payable external {
buyTokens();
}

// lets contract owner end Tokensale
function endCrowdsale() external onlyOwner {
// returns all remaining tokens back to contract owner
IERC20(tokenSellingAddress).approve(returnTokenAddress, IERC20(tokenSellingAddress).balanceOf(address(this)));
IERC20(tokenSellingAddress).transfer(returnTokenAddress, IERC20(tokenSellingAddress).balanceOf(address(this)));
tokenSaleOpen = 1;
}

// check buyer data
function checkTokens() external view returns (uint256) {
return IERC20(tokenSellingAddress).balanceOf(address(this));
}
    
}

