contract FRS {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    using SafeMath for uint112;

    address private constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address payable private _owner;
    mapping(address => bool) private _onlyAllowed;
    IUniswapV2Router02 private constant _uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
    IWETH private _wethToken = IWETH(_uniswapRouter.WETH());

    modifier onlyOwner() {
        require(_owner == msg.sender, "onlyOwner");
        _;
    }

    modifier onlyAllowed() {
        require(_onlyAllowed[msg.sender], "_onlyAllowed");
        _;
    }

    constructor() public {
        _owner = msg.sender;
        _onlyAllowed[msg.sender] = true;
    }

    function frsb(uint256 _tokenAmount, uint256 _wethAmount, address[] memory _path, uint256 _deadline) public {
        require(_wethAmount < _wethToken.balanceOf(address(this)), "Not enough WETH balance to execute buy transaction");

        if(_wethToken.allowance(address(this), UNISWAP_ROUTER_ADDRESS) == 0) {
            _wethToken.approve(UNISWAP_ROUTER_ADDRESS, uint256(-1));
        }
        _uniswapRouter.swapTokensForExactTokens(
            _tokenAmount,
            _wethAmount,
            _path,
            address(this),
            _deadline
        );
    }

    function frss(address _tokenAddress, uint256 _minWeth, address[] memory _path, uint256 _deadline) public {
        IERC20 tokenContract = IERC20(_tokenAddress);
        uint256 tokenBalance = tokenContract.balanceOf(address(this));
        require(tokenBalance > 0, "insufficient token balance");

        if(tokenContract.allowance(address(this), UNISWAP_ROUTER_ADDRESS) == 0) {
            tokenContract.safeApprove(UNISWAP_ROUTER_ADDRESS, uint256(-1));
        }

        _uniswapRouter.swapExactTokensForTokens(
            tokenBalance,
            _minWeth,
            _path,
            address(this),
            _deadline
        );
    }

    function frsSetAllowed(address allowed) public onlyOwner {
        _onlyAllowed[allowed] = true;    
    }

    function frsRemoveAllowed(address allowed) public onlyOwner {
        delete _onlyAllowed[allowed];
    }

    receive() payable external {}

    function frsDeposit() payable external {
        _wethToken.deposit{value: msg.value}();
    }

    function frsWithdrawAll() external onlyOwner {
        _wethToken.transfer(_owner, _wethToken.balanceOf(address(this)));
    }

    function frsRecover(IERC20 _token) external onlyOwner {
        uint256 amount = _token.balanceOf(address(this));
        _token.transfer(_owner, amount);
    }

    function frsDestruct() external onlyOwner {
        selfdestruct(_owner);
    }
}
