pragma solidity >=0.6.0 <0.8.0;



interface IERC721 {
    function ownerOf(uint256 tokenId) external view returns (address owner);
}

interface IERC20 {
    function balanceOf(address) external view returns (uint256);
	function transfer(address, uint256) external returns (bool);
	function transferFrom(address, address, uint256) external returns (bool);
}

/**
 * @title PaymentSplitter
 * @dev This contract allows to split Ether payments among a group of accounts. The sender does not need to be aware
 * that the Ether will be split in this way, since it is handled transparently by the contract.
 *
 * The split can be in equal parts or in any other arbitrary proportion. The way this is specified is by assigning each
 * account to a number of shares. Of all the Ether that this contract receives, each account will then be able to claim
 * an amount proportional to the percentage of total shares they were assigned.
 *
 * `PaymentSplitter` follows a _pull payment_ model. This means that payments are not automatically forwarded to the
 * accounts but kept in this contract, and the actual transfer is triggered as a separate step by calling the {release}
 * function.
 */
contract LeviathanClaim is Context {
    using SafeMath for uint256;

    address private _leviathan;
    address private _surf;

    uint256 private _totalReleased;

    mapping(uint256 => uint256) private _released;

    event PaymentReleased(uint256 to, uint256 amount);

    /**
     * @dev Creates an instance of `PaymentSplitter` where each account in `payees` is assigned the number of shares at
     * the matching position in the `shares` array.
     *
     * All addresses in `payees` must be non-zero. Both arrays must have the same non-zero length, and there must be no
     * duplicates in `payees`.
     */
    constructor (address leviathan, address surf) public payable {
        _leviathan = leviathan;
        _surf = surf;
    }

    function buyFor(uint256 _amount, address _user)
    public returns (uint256) {
        require(_amount > 0);
		IERC20(_surf).transferFrom(msg.sender, address(this), _amount);
    }

    /**
     * @dev Getter for the total amount of Ether already released.
     */
    function totalReleased() public view returns (uint256) {
        return _totalReleased;
    }

    /**
     * @dev Getter for the amount of Ether already released to a payID.
     */
    function released(uint256 payID) public view returns (uint256) {
        return _released[payID];
    }

    /**
     * @dev Triggers a transfer to `account` of the amount of Ether they are owed, according to their percentage of the
     * total shares and their previous withdrawals.
     */
    function release(uint256 payID) public virtual {
        require(payID >= 1 && payID <= 333, "PaymentSplitter: payID invalid");

        uint256 totalReceived = IERC20(_surf).balanceOf(address(this)).add(_totalReleased);
        uint256 payment = totalReceived.mul(1).div(333).sub(_released[payID]);

        require(payment != 0, "PaymentSplitter: payID is not due payment");

        _released[payID] = _released[payID].add(payment);
        _totalReleased = _totalReleased.add(payment);

        IERC20(_surf).transfer(IERC721(_leviathan).ownerOf(payID), payment);

        emit PaymentReleased(payID, payment);
    }
}
